// src/components/sections/Skills.jsx
import React from "react";
import { useTheme } from "../../context/ThemeContext";

const Skills = () => {
  const { darkMode } = useTheme();

  // Définition des icônes inline SVG (plus petites)
  const Braces = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-5 h-5">
      <path d="M7 8H3a2 2 0 0 0-2 2v4a2 2 0 0 0 2 2h4"></path>
      <path d="M17 16h4a2 2 0 0 0 2-2v-4a2 2 0 0 0-2-2h-4"></path>
      <path d="M3 16V8a2 2 0 0 1 2-2h2"></path>
      <path d="M15 4h2a2 2 0 0 1 2 2v8"></path>
    </svg>
  );

  const Server = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-5 h-5">
      <rect x="2" y="2" width="20" height="8" rx="2" ry="2"></rect>
      <rect x="2" y="14" width="20" height="8" rx="2" ry="2"></rect>
      <line x1="6" y1="6" x2="6.01" y2="6"></line>
      <line x1="6" y1="18" x2="6.01" y2="18"></line>
    </svg>
  );

  const Globe = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-5 h-5">
      <circle cx="12" cy="12" r="10"></circle>
      <line x1="2" y1="12" x2="22" y2="12"></line>
      <path d="M12 2a15.3 15.3 0 0 1 4 10 15.3 15.3 0 0 1-4 10 15.3 15.3 0 0 1-4-10 15.3 15.3 0 0 1 4-10z"></path>
    </svg>
  );

  // Définition des catégories de compétences
  const skillCategories = [
    {
      title: "Frontend",
      icon: <Braces />,
      color: "text-blue-500",
      bgColor: "bg-blue-500",
      borderColor: "border-blue-500",
      skills: [
        {
          name: "React & Next.js",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/react/react-original.svg",
          isImage: true,
          description: "Développement d'applications single-page performantes"
        },
        {
          name: "Vue.js & Nuxt",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/vuejs/vuejs-original.svg",
          isImage: true,
          description: "Création d'interfaces réactives et progressives"
        },
        {
          name: "Bootstrap",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/bootstrap/bootstrap-original.svg",
          isImage: true,
          description: "Design responsive et composants prêts à l'emploi"
        },
        {
          name: "TypeScript",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/typescript/typescript-original.svg",
          isImage: true,
          description: "Typage statique pour un code plus robuste"
        },
        {
          name: "Tailwind CSS",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/tailwindcss/tailwindcss-plain.svg",
          isImage: true,
          description: "Styling flexible et utilitaire"
        },
      ],
    },
    {
      title: "Backend",
      icon: <Server />,
      color: "text-green-500",
      bgColor: "bg-green-500",
      borderColor: "border-green-500",
      skills: [
        {
          name: "Node.js",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/nodejs/nodejs-original.svg",
          isImage: true,
          description: "Applications serveur haute performance"
        },
        {
          name: "PHP",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/php/php-original.svg",
          isImage: true,
          description: "Développement web serveur robuste"
        },
        {
          name: "Symfony",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/symfony/symfony-original.svg",
          isImage: true,
          description: "Framework PHP puissant et flexible"
        },
        {
          name: "Laravel",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/laravel/laravel-plain.svg",
          isImage: true,
          description: "Développement web élégant et rapide"
        },
        {
          name: "MongoDB",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mongodb/mongodb-original.svg",
          isImage: true,
          description: "Base de données NoSQL scalable"
        },
      ],
    },
    {
      title: "CMS & Outils",
      icon: <Globe />,
      color: "text-purple-500",
      bgColor: "bg-purple-500",
      borderColor: "border-purple-500",
      skills: [
        {
          name: "WordPress",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/wordpress/wordpress-plain.svg",
          isImage: true,
          description: "Création de sites web et blogs"
        },
        {
          name: "Shopify",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/shopify/shopify-original.svg",
          isImage: true,
          description: "Solutions e-commerce complètes"
        },
        {
          name: "Git & GitHub",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/git/git-original.svg",
          isImage: true,
          description: "Gestion de version et collaboration"
        },
        {
          name: "Docker",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/docker/docker-original.svg",
          isImage: true,
          description: "Conteneurisation et déploiement"
        },
        {
          name: "Figma",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/figma/figma-original.svg",
          isImage: true,
          description: "Conception d'interfaces et prototypage"
        },
      ],
    },
  ];

  return (
    <section
      id="skills"
      className="py-16"
    >
      <div className="max-w-6xl mx-auto px-4">
        <div className="text-center mb-10">
          <h2 className={`text-3xl font-bold mb-3 ${darkMode ? "text-white" : "text-gray-800"}`}>Expertise Technique</h2>
          <p
            className={`max-w-2xl mx-auto text-sm ${
              darkMode ? "text-gray-400" : "text-gray-600"
            }`}
          >
            Fort d'une expertise technique approfondie et d'un sens aigu du
            design, je développe des applications web modernes et
            performantes.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {skillCategories.map((category, index) => (
            <div
              key={index}
              className={`rounded-lg shadow-md transition-all duration-300 hover:shadow-lg ${
                darkMode ? "bg-gray-800 border border-gray-700" : "bg-white border border-gray-100"
              }`}
            >
              {/* En-tête de la carte (plus compact) */}
              <div 
                className={`${category.bgColor} py-3 px-4 flex justify-between items-center rounded-t-lg`}
              >
                <div className="flex items-center gap-2">
                  <div 
                    className={`w-8 h-8 rounded-md flex items-center justify-center ${
                      darkMode ? "bg-white/20" : "bg-white"
                    } shadow-md`}
                  >
                    <div className={category.color}>{category.icon}</div>
                  </div>
                  <h3 className="text-base font-bold text-white">
                    {category.title}
                  </h3>
                </div>
                <div 
                  className={`w-6 h-6 rounded-full ${darkMode ? "bg-white/20" : "bg-white"} 
                  flex items-center justify-center shadow-md`}
                >
                  <span className={`${category.color} text-xs font-bold`}>{category.skills.length}</span>
                </div>
              </div>

              {/* Contenu de la carte (liste des compétences) */}
              <div className="p-3">
                <div className="space-y-2">
                  {category.skills.map((skill, skillIndex) => (
                    <div
                      key={skillIndex}
                      className={`group/skill p-2 rounded-md ${
                        darkMode 
                          ? "bg-gray-700/50 hover:bg-gray-700" 
                          : "bg-gray-50 hover:bg-gray-100"
                      } transition-all duration-300`}
                    >
                      <div className="flex items-center gap-3">
                        <div
                          className={`flex items-center justify-center w-7 h-7 rounded-md ${
                            darkMode 
                              ? `bg-gray-900/30 border border-gray-700` 
                              : `bg-white border border-gray-200`
                          }`}
                        >
                          {skill.isImage ? (
                            <img
                              src={skill.icon}
                              className="w-4 h-4"
                              alt={skill.name}
                            />
                          ) : (
                            <div className={category.color}>
                              {skill.icon}
                            </div>
                          )}
                        </div>
                        <div className="flex-1">
                          <span className={`text-sm font-medium ${darkMode ? "text-white" : "text-gray-800"}`}>
                            {skill.name}
                          </span>
                          <p className={`text-xs ${darkMode ? "text-gray-400" : "text-gray-500"} 
                            max-h-0 overflow-hidden group-hover/skill:max-h-10 transition-all duration-200`}>
                            {skill.description}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {/* Pied de la carte (plus discret) */}
              <div 
                className={`px-4 py-2 ${darkMode ? "bg-gray-700/30" : "bg-gray-50"} border-t ${
                  darkMode ? "border-gray-700" : "border-gray-200"
                } rounded-b-lg`}
              >
                <div className="flex justify-between items-center">
                  <span className={`text-xs font-medium ${category.color}`}>
                    {index === 0 ? "Front-end" : 
                     index === 1 ? "Back-end" : 
                     "Outils & Frameworks"}
                  </span>
                  <span className={`text-xs px-1.5 py-0.5 rounded-sm ${
                    darkMode 
                      ? `bg-gray-900/50 text-${category.color.split('-')[1]}-400` 
                      : `bg-${category.color.split('-')[1]}-50 text-${category.color.split('-')[1]}-700`
                  }`}>
                    Expert
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
