// src/components/sections/Skills.jsx
import React, { useState } from "react";
import { useTheme } from "../../context/ThemeContext";

const Skills = () => {
  const { darkMode } = useTheme();
  const [activeCategory, setActiveCategory] = useState(0);

  // Icônes SVG optimisées
  const Braces = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-6 h-6">
      <path d="M7 8H3a2 2 0 0 0-2 2v4a2 2 0 0 0 2 2h4"></path>
      <path d="M17 16h4a2 2 0 0 0 2-2v-4a2 2 0 0 0-2-2h-4"></path>
      <path d="M3 16V8a2 2 0 0 1 2-2h2"></path>
      <path d="M15 4h2a2 2 0 0 1 2 2v8"></path>
    </svg>
  ); 

  const Server = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-6 h-6">
      <rect x="2" y="2" width="20" height="8" rx="2" ry="2"></rect>
      <rect x="2" y="14" width="20" height="8" rx="2" ry="2"></rect>
      <line x1="6" y1="6" x2="6.01" y2="6"></line>
      <line x1="6" y1="18" x2="6.01" y2="18"></line>
    </svg>
  );

  const Globe = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-6 h-6">
      <circle cx="12" cy="12" r="10"></circle>
      <line x1="2" y1="12" x2="22" y2="12"></line>
      <path d="M12 2a15.3 15.3 0 0 1 4 10 15.3 15.3 0 0 1-4 10 15.3 15.3 0 0 1-4-10 15.3 15.3 0 0 1 4-10z"></path>
    </svg>
  );

  const Sparkles = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-5 h-5">
      <path d="M12 3l1.912 5.813a2 2 0 001.275 1.275L21 12l-5.813 1.912a2 2 0 00-1.275 1.275L12 21l-1.912-5.813a2 2 0 00-1.275-1.275L3 12l5.813-1.912a2 2 0 001.275-1.275L12 3z"></path>
    </svg>
  );

  const skillCategories = [
    {
      title: "Frontend",
      icon: <Braces />,
      gradient: "from-blue-500 to-cyan-500",
      color: "text-blue-500",
      hoverColor: "hover:text-blue-600",
      bgColor: "bg-gradient-to-br from-blue-500 to-cyan-500",
      lightBg: "bg-blue-50",
      darkBg: "bg-blue-950/30",
      borderColor: "border-blue-200",
      darkBorderColor: "border-blue-800",
      skills: [
        {
          name: "React & Next.js",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/react/react-original.svg",
          level: 95,
          experience: "3+ ans",
          projects: "15+ projets",
          description: "Applications SPA performantes avec SSR"
        },
        {
          name: "Vue.js & Nuxt",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/vuejs/vuejs-original.svg",
          level: 90,
          experience: "2+ ans",
          projects: "10+ projets",
          description: "Interfaces réactives et progressives"
        },
        {
          name: "TypeScript",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/typescript/typescript-original.svg",
          level: 88,
          experience: "2+ ans",
          projects: "20+ projets",
          description: "Code robuste et maintenable"
        },
        {
          name: "Tailwind CSS",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/tailwindcss/tailwindcss-original.svg",
          level: 92,
          experience: "2+ ans",
          projects: "25+ projets",
          description: "Design system moderne"
        },
        {
          name: "Bootstrap",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/bootstrap/bootstrap-original.svg",
          level: 85,
          experience: "3+ ans",
          projects: "18+ projets",
          description: "Framework responsive"
        },
      ],
    },
    {
      title: "Backend",
      icon: <Server />,
      gradient: "from-green-500 to-emerald-500",
      color: "text-green-500",
      hoverColor: "hover:text-green-600",
      bgColor: "bg-gradient-to-br from-green-500 to-emerald-500",
      lightBg: "bg-green-50",
      darkBg: "bg-green-950/30",
      borderColor: "border-green-200",
      darkBorderColor: "border-green-800",
      skills: [
        {
          name: "Node.js",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/nodejs/nodejs-original.svg",
          level: 90,
          experience: "3+ ans",
          projects: "12+ projets",
          description: "APIs REST et GraphQL performantes"
        },
        {
          name: "PHP",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/php/php-original.svg",
          level: 88,
          experience: "4+ ans",
          projects: "20+ projets",
          description: "Développement serveur robuste"
        },
        {
          name: "Symfony",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/symfony/symfony-original.svg",
          level: 85,
          experience: "2+ ans",
          projects: "8+ projets",
          description: "Architecture enterprise solide"
        },
        {
          name: "Laravel",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/laravel/laravel-original.svg",
          level: 87,
          experience: "2+ ans",
          projects: "10+ projets",
          description: "Développement rapide et élégant"
        },
        {
          name: "MongoDB",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mongodb/mongodb-original.svg",
          level: 82,
          experience: "2+ ans",
          projects: "8+ projets",
          description: "Base NoSQL scalable"
        },
      ],
    },
    {
      title: "CMS & Outils",
      icon: <Globe />,
      gradient: "from-purple-500 to-pink-500",
      color: "text-purple-500",
      hoverColor: "hover:text-purple-600",
      bgColor: "bg-gradient-to-br from-purple-500 to-pink-500",
      lightBg: "bg-purple-50",
      darkBg: "bg-purple-950/30",
      borderColor: "border-purple-200",
      darkBorderColor: "border-purple-800",
      skills: [
        {
          name: "WordPress",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/wordpress/wordpress-plain.svg",
          level: 90,
          experience: "4+ ans",
          projects: "30+ projets",
          description: "Thèmes et plugins custom"
        },
        {
          name: "Shopify",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon@latest/icons/shopify/shopify-original.svg",
          level: 85,
          experience: "2+ ans",
          projects: "12+ projets",
          description: "Solutions e-commerce complètes"
        },
        {
          name: "Git & GitHub",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/git/git-original.svg",
          level: 92,
          experience: "4+ ans",
          projects: "Tous projets",
          description: "Workflow collaboratif optimisé"
        },
        {
          name: "Docker",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/docker/docker-original.svg",
          level: 80,
          experience: "2+ ans",
          projects: "15+ projets",
          description: "Conteneurisation et CI/CD"
        },
        {
          name: "Figma",
          icon: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/figma/figma-original.svg",
          level: 88,
          experience: "3+ ans",
          projects: "25+ designs",
          description: "Design systems et prototypage"
        },
      ],
    },
  ];

  return (
    <section id="skills" className="py-20 relative overflow-hidden">
      {/* Background decoratif */}
      <div className="absolute inset-0 pointer-events-none">
        <div className={`absolute top-20 right-10 w-72 h-72 ${darkMode ? 'bg-blue-500/5' : 'bg-blue-500/10'} rounded-full blur-3xl`}></div>
        <div className={`absolute bottom-20 left-10 w-96 h-96 ${darkMode ? 'bg-purple-500/5' : 'bg-purple-500/10'} rounded-full blur-3xl`}></div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
        {/* En-tête amélioré */}
        <div className="text-center mb-16">
          <div className="inline-flex items-center gap-2 mb-4 px-4 py-2 rounded-full bg-gradient-to-r from-blue-500/10 to-purple-500/10 border border-blue-500/20">
            <Sparkles />
            <span className={`text-sm font-semibold ${darkMode ? 'text-blue-400' : 'text-blue-600'}`}>
              Expertise Technique
            </span>
          </div>

          <h2 className={`text-4xl md:text-5xl font-bold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Compétences & Technologies
          </h2>

          <p className={`max-w-2xl mx-auto text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Une expertise complète du développement web moderne, de la conception à la mise en production
          </p>
        </div>

        {/* Onglets de navigation */}
        <div className="flex justify-center mb-12">
          <div className={`inline-flex p-1 rounded-xl ${darkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'} shadow-lg`}>
            {skillCategories.map((category, index) => (
              <button
                key={index}
                onClick={() => setActiveCategory(index)}
                className={`flex items-center gap-2 px-6 py-3 rounded-lg font-semibold transition-all duration-300 ${
                  activeCategory === index
                    ? `${category.bgColor} text-white shadow-lg scale-105`
                    : darkMode
                    ? 'text-gray-400 hover:text-white hover:bg-gray-700'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                <div className={activeCategory === index ? 'text-white' : category.color}>
                  {category.icon}
                </div>
                <span className="hidden sm:inline">{category.title}</span>
                <span className={`text-xs px-2 py-0.5 rounded-full ${
                  activeCategory === index 
                    ? 'bg-white/20' 
                    : darkMode ? 'bg-gray-700' : 'bg-gray-100'
                }`}>
                  {category.skills.length}
                </span>
              </button>
            ))}
          </div>
        </div>

        {/* Contenu des compétences avec animation */}
        <div className="relative">
          {skillCategories.map((category, catIndex) => (
            <div
              key={catIndex}
              className={`transition-all duration-500 ${
                activeCategory === catIndex
                  ? 'opacity-100 translate-y-0'
                  : 'opacity-0 absolute inset-0 pointer-events-none translate-y-8'
              }`}
            >
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {category.skills.map((skill, skillIndex) => (
                  <div
                    key={skillIndex}
                    className={`group relative rounded-2xl p-6 transition-all duration-300 hover:scale-105 ${
                      darkMode
                        ? 'bg-gray-800 border border-gray-700 hover:border-gray-600'
                        : 'bg-white border border-gray-200 hover:border-gray-300'
                    } shadow-lg hover:shadow-2xl`}
                  >
                    {/* Badge de niveau */}
                    <div className="absolute -top-3 -right-3 z-10">
                      <div className={`${category.bgColor} text-white text-xs font-bold px-3 py-1.5 rounded-full shadow-lg`}>
                        {skill.level}%
                      </div>
                    </div>

                    {/* Icône et nom */}
                    <div className="flex items-start gap-4 mb-4">
                      <div className={`flex-shrink-0 w-14 h-14 rounded-xl flex items-center justify-center ${
                        darkMode ? category.darkBg : category.lightBg
                      } border ${darkMode ? category.darkBorderColor : category.borderColor} group-hover:scale-110 transition-transform duration-300`}>
                        <img
                          src={skill.icon}
                          alt={skill.name}
                          className="w-8 h-8"
                        />
                      </div>
                      <div className="flex-1">
                        <h4 className={`font-bold text-lg mb-1 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                          {skill.name}
                        </h4>
                        <p className={`text-xs ${darkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                          {skill.description}
                        </p>
                      </div>
                    </div>

                    {/* Barre de progression */}
                    <div className="mb-4">
                      <div className={`h-2 rounded-full overflow-hidden ${
                        darkMode ? 'bg-gray-700' : 'bg-gray-100'
                      }`}>
                        <div
                          className={`h-full ${category.bgColor} transition-all duration-1000 ease-out rounded-full`}
                          style={{ 
                            width: activeCategory === catIndex ? `${skill.level}%` : '0%',
                            transitionDelay: `${skillIndex * 100}ms`
                          }}
                        ></div>
                      </div>
                    </div>

                    {/* Statistiques */}
                    <div className="flex items-center justify-between text-sm">
                      <div className="flex items-center gap-1">
                        <svg className={`w-4 h-4 ${category.color}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        <span className={darkMode ? 'text-gray-400' : 'text-gray-600'}>
                          {skill.experience}
                        </span>
                      </div>
                      <div className="flex items-center gap-1">
                        <svg className={`w-4 h-4 ${category.color}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                        </svg>
                        <span className={darkMode ? 'text-gray-400' : 'text-gray-600'}>
                          {skill.projects}
                        </span>
                      </div>
                    </div>

                    {/* Effet de hover brillant */}
                    <div className="absolute inset-0 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none">
                      <div className={`absolute inset-0 rounded-2xl bg-gradient-to-br ${category.gradient} opacity-5`}></div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Statistiques de la catégorie */}
              <div className={`mt-8 p-6 rounded-2xl ${darkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'} shadow-lg`}>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <div className="text-center">
                    <div className={`text-3xl font-bold mb-1 ${category.color}`}>
                      {category.skills.length}
                    </div>
                    <div className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      Technologies maîtrisées
                    </div>
                  </div>
                  <div className="text-center">
                    <div className={`text-3xl font-bold mb-1 ${category.color}`}>
                      {Math.round(category.skills.reduce((acc, s) => acc + s.level, 0) / category.skills.length)}%
                    </div>
                    <div className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      Niveau moyen d'expertise
                    </div>
                  </div>
                  <div className="text-center">
                    <div className={`text-3xl font-bold mb-1 ${category.color}`}>
                      {category.skills.reduce((acc, s) => {
                        const num = parseInt(s.projects.split('+')[0]) || 0;
                        return acc + num;
                      }, 0)}+
                    </div>
                    <div className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      Projets réalisés
                    </div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Call to action */}
        <div className="text-center mt-16">
          <p className={`mb-6 text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Intéressé par mon profil ?
          </p>


            href="#contact"
            className={`inline-flex items-center gap-2 px-8 py-4 rounded-xl font-semibold transition-all duration-300 ${
              darkMode
                ? 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-500 hover:to-purple-500 text-white'
                : 'bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white'
            } shadow-lg hover:shadow-xl hover:scale-105`}
          <a
            href="#contact"
            className={`inline-flex items-center gap-2 px-8 py-4 rounded-xl font-semibold transition-all duration-300 ${
              darkMode
                ? 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-500 hover:to-purple-500 text-white'
