import React from 'react'
import { useTheme } from '../../context/ThemeContext'

export default function Projects() {
  const { darkMode } = useTheme()

  const projects = [
    {
      id: 1,
      title: "Application E-commerce",
      description: "Plateforme de commerce en ligne avec panier et gestion des commandes.",
      tags: ["React", "Node.js", "MongoDB"],
      github: "https://github.com/username/ecommerce",
      demo: "https://demo.example.com"
    },
    {
      id: 2,
      title: "Dashboard Analytics",
      description: "Tableau de bord pour visualiser des données avec graphiques dynamiques.",
      tags: ["Next.js", "TypeScript", "Tailwind"],
      github: "https://github.com/username/dashboard",
      demo: "https://demo.example.com"
    },
    {
      id: 3,
      title: "API REST Sécurisée",
      description: "API avec authentification JWT et documentation Swagger.",
      tags: ["Express", "PostgreSQL", "Docker"],
      github: "https://github.com/username/api",
      demo: null
    }
  ]

  return (
    <section id="projects" className="min-h-screen p-8 py-20">
      <div className="max-w-4xl mx-auto">

        {/* Header simple */}
        <div className="mb-12">
          <h2 className={`text-3xl font-semibold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Projets
          </h2>
          <p className={`text-sm ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Quelques réalisations récentes
          </p>
        </div>

        {/* Liste de projets */}
        <div className="space-y-6">
          {projects.map((project) => (
            <div
              key={project.id}
              className={`p-6 border rounded-lg transition-all hover:shadow-md ${
                darkMode 
                  ? 'bg-gray-900 border-gray-800 hover:border-gray-700' 
                  : 'bg-white border-gray-200 hover:border-gray-300'
              }`}
            >
              <div className="flex items-start justify-between mb-3">
                <h3 className={`text-lg font-medium ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                  {project.title}
                </h3>
              </div>

              <p className={`text-sm mb-4 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                {project.description}
              </p>

              {/* Tags minimalistes */}
              <div className="flex flex-wrap gap-2 mb-4">
                {project.tags.map((tag, index) => (
                  <span
                    key={index}
                    className={`text-xs px-2 py-1 rounded ${
                      darkMode 
                        ? 'bg-gray-800 text-gray-300' 
                        : 'bg-gray-100 text-gray-700'
                    }`}
                  >
                    {tag}
                  </span>
                ))}
              </div>

              {/* Liens discrets */}
              <div className="flex gap-3 text-sm">
                <a
                  href={project.github}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`underline ${darkMode ? 'text-gray-300 hover:text-white' : 'text-gray-700 hover:text-black'}`}
                >
                  Code →
                </a>
                {project.demo && (
                  <a
                    href={project.demo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`underline ${darkMode ? 'text-gray-300 hover:text-white' : 'text-gray-700 hover:text-black'}`}
                  >
                    Démo →
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Contact simple */}
        <div className="mt-12 text-center">
          <p className={`text-sm mb-3 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Un projet en tête ?
          </p>
          <a
            href="#contact"
            className={`inline-block text-sm underline ${darkMode ? 'text-white hover:text-gray-300' : 'text-black hover:text-gray-700'}`}
          >
            Me contacter →
          </a>
        </div>
      </div>
    </section>
  )
}
