// src/components/sections/Skills.jsx
import React, { useState, useEffect } from "react";
import { skillCategories } from "../../data/skills";
import { useTheme } from "../../context/ThemeContext";

const Skills = () => {
  const { darkMode } = useTheme();
  const [selectedCategory, setSelectedCategory] = useState(0);
  const [animateSkills, setAnimateSkills] = useState(false);

  // Réinitialise l'animation lors du changement de catégorie
  useEffect(() => {
    setAnimateSkills(false);
    const timer = setTimeout(() => setAnimateSkills(true), 50);
    return () => clearTimeout(timer);
  }, [selectedCategory]);

  // Fonction pour générer un style de barre de progression
  const getProgressBarStyle = (level) => {
    const gradientColors = {
      frontend: "from-blue-500 to-indigo-600",
      backend: "from-purple-500 to-pink-600",
      tools: "from-emerald-500 to-teal-600"
    };

    const categoryKey = selectedCategory === 0 ? "frontend" : selectedCategory === 1 ? "backend" : "tools";

    return {
      width: `${level}%`,
      background: `bg-gradient-to-r ${gradientColors[categoryKey]}`
    };
  };

  return (
    <section
      id="skills"
      className={`min-h-screen flex items-center py-20 ${
        darkMode ? "bg-gray-900" : "bg-gray-50"
      }`}
    >
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 
            className={`text-5xl font-extrabold mb-6 ${
              darkMode ? "text-white" : "text-gray-900"
            }`}
          >
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-purple-600">
              Expertise Technique
            </span>
          </h2>
          <p 
            className={`max-w-3xl mx-auto text-xl ${
              darkMode ? "text-gray-300" : "text-gray-600"
            }`}
          >
            Fort d'une expertise technique approfondie et d'un sens aigu du design, 
            je transforme des idées complexes en applications élégantes et performantes.
          </p>
        </div>

        {/* Navigation des catégories */}
        <div className="max-w-3xl mx-auto mb-10">
          <div 
            className={`flex rounded-full p-1 mb-12 ${
              darkMode ? "bg-gray-800" : "bg-white shadow-md"
            }`}
          >
            {skillCategories.map((category, index) => (
              <button
                key={index}
                onClick={() => setSelectedCategory(index)}
                className={`flex-1 py-3 px-5 text-center rounded-full font-medium transition-all duration-300 ${
                  selectedCategory === index
                    ? `bg-gradient-to-r ${
                        index === 0 ? "from-blue-500 to-indigo-600" :
                        index === 1 ? "from-purple-500 to-pink-600" :
                        "from-emerald-500 to-teal-600"
                      } text-white shadow-lg`
                    : `${darkMode ? "text-gray-300 hover:text-white" : "text-gray-600 hover:text-gray-900"}`
                }`}
              >
                <span className="mr-2">{category.icon}</span>
                {category.title.split(" ")[0]}
              </button>
            ))}
          </div>
        </div>

        {/* Affichage des compétences */}
        <div 
          className={`max-w-4xl mx-auto rounded-2xl overflow-hidden shadow-2xl ${
            darkMode ? "bg-gray-800 border border-gray-700" : "bg-white"
          }`}
        >
          <div className={`p-1 ${
            selectedCategory === 0 ? "bg-gradient-to-r from-blue-500 to-indigo-600" :
            selectedCategory === 1 ? "bg-gradient-to-r from-purple-500 to-pink-600" :
            "bg-gradient-to-r from-emerald-500 to-teal-600"
          }`}></div>

          <div className="p-8">
            <h3 
              className={`text-2xl font-bold mb-8 text-center ${
                darkMode ? "text-white" : "text-gray-800"
              }`}
            >
              {skillCategories[selectedCategory].title}
            </h3>

            <div className="grid gap-8">
              {skillCategories[selectedCategory].skills.map((skill, index) => (
                <div key={index} className="relative">
                  <div className="flex justify-between mb-2">
                    <span className={`font-semibold ${
                      darkMode ? "text-white" : "text-gray-800"
                    }`}>
                      {skill.name}
                    </span>
                    <span className={`font-medium ${
                      darkMode ? "text-gray-300" : "text-gray-600"
                    }`}>
                      {skill.level}%
                    </span>
                  </div>

                  <div 
                    className={`h-3 w-full rounded-full ${
                      darkMode ? "bg-gray-700" : "bg-gray-200"
                    }`}
                  >
                    <div 
                      className={`h-full rounded-full transition-all duration-1000 ease-out ${getProgressBarStyle(skill.level).background} ${
                        animateSkills ? "opacity-100" : "opacity-0 w-0"
                      }`}
                      style={{ width: animateSkills ? `${skill.level}%` : '0%' }}
                    ></div>
                  </div>
                </div>
              ))}
            </div>

            <div 
              className={`mt-10 p-6 rounded-xl ${
                darkMode ? "bg-gray-700" : "bg-gray-50"
              }`}
            >
              <div className="flex items-start">
                <div 
                  className={`mr-4 mt-1 text-3xl ${
                    darkMode ? "text-gray-300" : "text-gray-700"
                  }`}
                >
                  {skillCategories[selectedCategory].icon}
                </div>
                <div>
                  <h4 
                    className={`text-xl font-semibold mb-2 ${
                      darkMode ? "text-white" : "text-gray-800"
                    }`}
                  >
                    {selectedCategory === 0 ? "Développement Frontend" :
                     selectedCategory === 1 ? "Développement Backend" :
                     "Outils & Méthodologies"}
                  </h4>
                  <p 
                    className={`${
                      darkMode ? "text-gray-300" : "text-gray-600"
                    }`}
                  >
                    {selectedCategory === 0 && "Création d'interfaces utilisateur modernes, réactives et accessibles avec les technologies frontend les plus avancées."}
                    {selectedCategory === 1 && "Développement de services robustes, sécurisés et évolutifs avec les frameworks backend les plus performants."}
                    {selectedCategory === 2 && "Utilisation d'outils et méthodologies qui optimisent le flux de travail, la collaboration et la qualité du code."}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
