import React from 'react'
import { useTheme } from '../../'

export default function Projects() {
  const { darkMode } = useTheme()
  const [isVisible, setIsVisible] = React.useState(false)

  React.useEffect(() => {
    setIsVisible(true)
  }, [])

  const projects = [
    {
      id: 1,
      title: "Application E-commerce",
      description: "Une plateforme de commerce en ligne complète avec panier, paiement et gestion des commandes.",
      image: "/projects/ecommerce.jpg",
      tags: ["React", "Node.js", "MongoDB", "Stripe"],
      github: "https://github.com/username/ecommerce",
      demo: "https://demo.example.com",
      category: "Full Stack"
    },
    {
      id: 2,
      title: "Dashboard Analytics",
      description: "Tableau de bord interactif pour visualiser des données en temps réel avec graphiques dynamiques.",
      image: "/projects/dashboard.jpg",
      tags: ["Next.js", "TypeScript", "Chart.js", "Tailwind"],
      github: "https://github.com/username/dashboard",
      demo: "https://demo.example.com",
      category: "Frontend"
    },
    {
      id: 3,
      title: "API REST Sécurisée",
      description: "API robuste avec authentification JWT, validation des données et documentation Swagger.",
      image: "/projects/api.jpg",
      tags: ["Express", "PostgreSQL", "JWT", "Docker"],
      github: "https://github.com/username/api",
      demo: null,
      category: "Backend"
    },
    {
      id: 4,
      title: "Application Mobile",
      description: "Application cross-platform pour la gestion de tâches avec synchronisation cloud.",
      image: "/projects/mobile.jpg",
      tags: ["React Native", "Firebase", "Redux", "Expo"],
      github: "https://github.com/username/mobile-app",
      demo: "https://demo.example.com",
      category: "Mobile"
    }
  ]

  const categories = ["Tous", "Full Stack", "Frontend", "Backend", "Mobile"]
  const [activeCategory, setActiveCategory] = React.useState("Tous")
  const [filteredProjects, setFilteredProjects] = React.useState(projects)

  React.useEffect(() => {
    if (activeCategory === "Tous") {
      setFilteredProjects(projects)
    } else {
      setFilteredProjects(projects.filter(project => project.category === activeCategory))
    }
  }, [activeCategory])

  return (
    <section
      id="projects"
      className={`section-content min-h-screen p-8 py-24 ${isVisible ? 'visible' : ''}`}
    >
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div 
          className="text-center mb-16 reveal"
          style={{
            transform: isVisible ? 'translateY(0) translateZ(30px)' : 'translateY(50px) translateZ(-30px)',
            opacity: isVisible ? 1 : 0,
            transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1)',
            transformStyle: 'preserve-3d'
          }}
        >
          <h1 className="text-5xl lg:text-7xl font-bold mb-6">
            Mes{" "}
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-purple-600">
              Projets
            </span>
          </h1>
          <p className={`text-lg lg:text-xl max-w-2xl mx-auto ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Découvrez une sélection de mes réalisations les plus récentes et significatives
          </p>
        </div>

        {/* Filtres */}
        <div 
          className="flex flex-wrap justify-center gap-3 mb-12 reveal"
          style={{
            transform: isVisible ? 'translateY(0) translateZ(20px)' : 'translateY(30px) translateZ(-20px)',
            opacity: isVisible ? 1 : 0,
            transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1) 0.2s',
            transformStyle: 'preserve-3d'
          }}
        >
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setActiveCategory(category)}
              className={`px-6 py-3 rounded-full font-medium transition-all duration-300 ${
                activeCategory === category
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-2xl scale-105'
                  : darkMode 
                    ? 'bg-gray-800 text-gray-300 hover:bg-gray-700 shadow-lg' 
                    : 'bg-white text-gray-700 hover:bg-gray-100 shadow-lg'
              }`}
            >
              {category}
            </button>
          ))}
        </div>

        {/* Grille de projets */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {filteredProjects.map((project, index) => (
            <div
              key={project.id}
              className={`group rounded-3xl overflow-hidden shadow-2xl transition-all duration-700 hover:scale-105 hover-effect ${
                darkMode ? 'bg-gray-800' : 'bg-white'
              }`}
              style={{
                transform: isVisible ? 'translateY(0) translateZ(20px)' : 'translateY(50px) translateZ(-20px)',
                opacity: isVisible ? 1 : 0,
                transition: `all 0.8s cubic-bezier(0.4, 0, 0.2, 1) ${0.3 + index * 0.1}s`,
                transformStyle: 'preserve-3d'
              }}
            >
              {/* Image avec effet de gradient */}
              <div className="relative h-64 overflow-hidden">
                <img src={project.image} alt={project.title} className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-105" />
                <div 
                  className={`absolute inset-0 transition-opacity duration-500 ${
                    darkMode ? "opacity-30" : "opacity-20"
                  } group-hover:opacity-40`}
                  style={{
                    background: "radial-gradient(circle at center, rgba(123, 97, 255, 0.4) 0%, rgba(123, 97, 255, 0) 70%)"
                  }}
                />
              </div>

              {/* Contenu */}
              <div className="p-6">
                <div className="flex items-start justify-between mb-3">
                  <h3 className={`text-2xl font-bold transition-colors ${
                    darkMode ? 'text-white group-hover:text-blue-400' : 'text-gray-900 group-hover:text-blue-600'
                  }`}>
                    {project.title}
                  </h3>
                  <span className="px-3 py-1 bg-gradient-to-r from-blue-500 to-purple-600 text-white text-xs font-semibold rounded-full">
                    {project.category}
                  </span>
                </div>

                <p className={`mb-6 leading-relaxed ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                  {project.description}
                </p>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-6">
                  {project.tags.map((tag, tagIndex) => (
                    <span
                      key={tagIndex}
                      className={`px-3 py-1 text-sm rounded-lg transition-colors ${
                        darkMode 
                          ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                {/* Liens */}
                <div className="flex gap-4">
                  <a
                    href={project.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`flex-1 flex items-center justify-center gap-2 px-4 py-3 rounded-xl transition-all duration-300 font-medium ${
                      darkMode 
                        ? 'bg-gray-700 text-white hover:bg-gray-600' 
                        : 'bg-gray-900 text-white hover:bg-gray-800'
                    }`}
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                    </svg>
                    Code
                  </a>

                  {project.demo && (
                    <a
                      href={project.demo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex-1 flex items-center justify-center gap-2 px-4 py-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-xl hover:shadow-xl transition-all duration-300 font-medium"
                    >
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                      </svg>
                      Demo
                    </a>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Call to action */}
        <div 
          className="text-center mt-16 reveal"
          style={{
            transform: isVisible ? 'translateY(0) translateZ(20px)' : 'translateY(30px) translateZ(-20px)',
            opacity: isVisible ? 1 : 0,
            transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1) 0.8s',
            transformStyle: 'preserve-3d'
          }}
        >
          <p className={`text-lg mb-6 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Vous avez un projet en tête ?
          </p>
          <a
            href="#contact"
            className="inline-block px-8 py-4 bg-gradient-to-r from-blue-500 to-purple-600 text-white font-semibold rounded-full hover:shadow-2xl transition-all duration-300 transform hover:scale-105"
          >
            Travaillons ensemble
          </a>
        </div>
      </div>
    </section>
  )
}
