import React from "react";
import { ExternalLink, Github } from "lucide-react";
import CommentSection from '../sections/CommentSection';

const ProjectCard = ({ project, index, darkMode }) => {
  return (
    <div
      className={`group relative overflow-hidden rounded-xl shadow-lg transition-all duration-300 hover:shadow-xl ${
        darkMode ? "bg-gray-800" : "bg-white"
      }`}
    >
      <div className="aspect-video w-full overflow-hidden">
        <img
          src={project.image}
          alt={project.title}
          className="h-full w-full object-cover transition-transform duration-500 group-hover:scale-110"
        />
      </div>

      <div className="p-6">
        <h3 className="mb-3 text-xl font-bold">{project.title}</h3>
        <p
          className={`mb-4 text-sm ${
            darkMode ? "text-gray-400" : "text-gray-600"
          }`}
        >
          {project.description}
        </p>

        <div className="mb-4 flex flex-wrap gap-2">
          {project.technologies.map((tech, techIndex) => (
            <span
              key={techIndex}
              className={`rounded-full px-3 py-1 text-xs ${
                darkMode
                  ? "bg-gray-700 text-gray-300"
                  : "bg-gray-100 text-gray-700"
              }`}
            >
              {tech}
            </span>
          ))}
        </div>

        <div className="flex gap-3">
          <a
            href={project.githubLink}
            target="_blank"
            rel="noopener noreferrer"
            className={`inline-flex items-center gap-2 rounded-lg px-3 py-1 text-sm transition-colors ${
              darkMode
                ? "bg-gray-700 hover:bg-gray-600 text-white"
                : "bg-gray-100 hover:bg-gray-200 text-gray-800"
            }`}
          >
            <Github size={16} />
            Code
          </a>
          <a
            href={project.liveLink}
            target="_blank"
            rel="noopener noreferrer"
            className={`inline-flex items-center gap-2 rounded-lg px-3 py-1 text-sm transition-colors ${
              darkMode
                ? "bg-gray-700 hover:bg-gray-600 text-white"
                : "bg-gray-100 hover:bg-gray-200 text-gray-800"
            }`}
          >
            <ExternalLink size={16} />
            Demo
          </a>
        </div>
      </div>

      {/* Section des commentaires */}
      <div className="mt-4">
        <CommentSection projectId={project.id} />
      </div>
    </div>
  );
};

export default ProjectCard;
