// src/components/sections/Skills.jsx
import React from "react";
import SkillCard from "../ui/SkillCard";
import { skillCategories } from "../../data/skills";
import { useTheme } from "../../context/ThemeContext";
import { FaReact, FaHtml5, FaCss3Alt, FaJs, FaNodeJs, FaDatabase } from "react-icons/fa";

const Skills = () => {
  const { darkMode } = useTheme();

  // Mapping des icônes pour chaque catégorie
  const categoryIcons = {
    "Frontend": <FaReact className="text-4xl text-blue-500 mb-4" />,
    "Backend": <FaNodeJs className="text-4xl text-green-600 mb-4" />,
    "Database": <FaDatabase className="text-4xl text-yellow-500 mb-4" />,
    "Design": <FaCss3Alt className="text-4xl text-purple-500 mb-4" />,
    "Languages": <FaJs className="text-4xl text-yellow-400 mb-4" />,
    "Tools": <FaHtml5 className="text-4xl text-red-500 mb-4" />
  };

  return (
    <section
      id="skills"
      className="section-content min-h-screen flex items-center justify-center p-8 py-24"
    >
      <div className="max-w-7xl mx-auto px-6 reveal" style={{ transformStyle: 'preserve-3d' }}>
        <div className="text-center mb-20">
          <h2 className="text-4xl font-bold mb-4">Expertise</h2>
          <p
            className={`max-w-2xl mx-auto ${
              darkMode ? "text-gray-400" : "text-gray-600"
            }`}
          >
            Fort d'une expertise technique approfondie et d'un sens aigu du
            design, je développe des applications web modernes et
            performantes.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-10" style={{ perspective: '1000px' }}>
          {skillCategories.map((category, index) => (
            <div 
              key={index}
              className="skill-card bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2"
            >
              <div className="flex flex-col items-center">
                {categoryIcons[category.title] || null}
                <h3 className="text-xl font-bold mb-4">{category.title}</h3>
                <ul className="space-y-2">
                  {category.skills.map((skill, skillIndex) => (
                    <li key={skillIndex} className="flex items-center">
                      <span className="w-2 h-2 bg-blue-500 rounded-full mr-2"></span>
                      {skill}
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
