import React, { useState } from 'react'
import { useTheme } from '../../context/ThemeContext'

export default function Projects() {
  const { darkMode } = useTheme()
  const [comments, setComments] = useState({})

  const projects = [
    {
      id: 1,
      title: "E-commerce Platform",
      description: "Plateforme compl√®te avec panier et paiement s√©curis√©.",
      image: "https://images.unsplash.com/photo-1557821552-17105176677c?w=800&h=600&fit=crop",
      tags: ["React", "Node.js", "Stripe"],
      github: "https://github.com/username/ecommerce",
      demo: "https://demo.example.com"
    },
    {
      id: 2,
      title: "Dashboard Analytics",
      description: "Visualisation de donn√©es en temps r√©el.",
      image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=800&h=600&fit=crop",
      tags: ["Next.js", "Chart.js", "TypeScript"],
      github: "https://github.com/username/dashboard",
      demo: "https://demo.example.com"
    },
    {
      id: 3,
      title: "Mobile Fitness App",
      description: "Application de suivi sportif et nutrition.",
      image: "https://images.unsplash.com/photo-1461896836934-ffe607ba8211?w=800&h=600&fit=crop",
      tags: ["React Native", "Firebase"],
      github: "https://github.com/username/fitness",
      demo: null
    },
    {
      id: 4,
      title: "API REST JWT",
      description: "API s√©curis√©e avec authentification et gestion d'utilisateurs.",
      image: "https://images.unsplash.com/photo-1581093588401-22c7fcbf2b3b?w=800&h=600&fit=crop",
      tags: ["Express", "MongoDB", "JWT"],
      github: "https://github.com/username/api",
      demo: null
    },
    {
      id: 5,
      title: "Portfolio Interactif",
      description: "Mon portfolio personnel avec animations fluides.",
      image: "https://images.unsplash.com/photo-1506765515384-028b60a970df?w=800&h=600&fit=crop",
      tags: ["React", "Tailwind", "Framer Motion"],
      github: "https://github.com/username/portfolio",
      demo: "https://demo.example.com"
    },
    {
      id: 6,
      title: "Site Vitrine Entreprise",
      description: "Site web moderne et responsive pour une startup tech.",
      image: "https://images.unsplash.com/photo-1521737604893-d14cc237f11d?w=800&h=600&fit=crop",
      tags: ["HTML", "CSS", "Bootstrap"],
      github: "https://github.com/username/vitrine",
      demo: "https://demo.example.com"
    }
  ]

  const handleCommentSubmit = (projectId, text) => {
    if (!text.trim()) return
    setComments((prev) => ({
      ...prev,
      [projectId]: [...(prev[projectId] || []), text]
    }))
  }

  return (
    <section id="projects" className="py-16 relative overflow-hidden">
      {/* Background Effects */}
      <div className="absolute inset-0 pointer-events-none">
        <div className={`absolute top-20 right-10 w-72 h-72 ${darkMode ? 'bg-blue-500/5' : 'bg-blue-500/10'} rounded-full blur-3xl`} />
        <div className={`absolute bottom-20 left-10 w-96 h-96 ${darkMode ? 'bg-purple-500/5' : 'bg-purple-500/10'} rounded-full blur-3xl`} />
      </div>

      <div className="max-w-7xl mx-auto px-6 relative">
        {/* Header */}
        <div className="text-center mb-14">
          <h2 className={`text-3xl sm:text-4xl font-bold mb-3 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Mes Projets
          </h2>
          <p className={`text-base sm:text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            D√©couvrez mes cr√©ations les plus r√©centes et ambitieuses üöÄ
          </p>
        </div>

        {/* Grid 3x2 */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <div
              key={project.id}
              className={`group overflow-hidden rounded-2xl border transition-all duration-300 hover:scale-[1.01] ${
                darkMode 
                  ? 'bg-gray-800/70 border-gray-700 hover:border-gray-600' 
                  : 'bg-white border-gray-200 hover:border-gray-300'
              } shadow-lg hover:shadow-xl`}
              style={{
                animation: `fadeInUp 0.6s ease-out ${index * 0.1}s both`
              }}
            >
              {/* Image */}
              <div className="relative h-48 overflow-hidden">
                <img 
                  src={project.image} 
                  alt={project.title}
                  className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                />
              </div>

              {/* Content */}
              <div className="p-6">
                <h3 className={`text-lg font-semibold mb-2 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
                  {project.title}
                </h3>
                <p className={`text-sm mb-4 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                  {project.description}
                </p>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.tags.map((tag, i) => (
                    <span
                      key={i}
                      className={`text-xs px-3 py-1 rounded-full ${
                        darkMode 
                          ? 'bg-gray-700 text-gray-300' 
                          : 'bg-gray-100 text-gray-700'
                      }`}
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                {/* Buttons */}
                <div className="flex gap-3 mb-6">
                  <a
                    href={project.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`flex-1 flex items-center justify-center gap-2 px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                      darkMode 
                        ? 'bg-gray-700 text-white hover:bg-gray-600' 
                        : 'bg-gray-900 text-white hover:bg-gray-800'
                    }`}
                  >
                    üíª Code
                  </a>

                  {project.demo && (
                    <a
                      href={project.demo}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex-1 flex items-center justify-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg text-sm font-medium hover:bg-blue-600 transition-all"
                    >
                      üåç D√©mo
                    </a>
                  )}
                </div>

                {/* Comments */}
                <div>
                  <h4 className={`text-sm font-semibold mb-2 ${darkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                    üí¨ Commentaires
                  </h4>

                  <div className="space-y-2 mb-3 max-h-24 overflow-y-auto">
                    {(comments[project.id] || []).length > 0 ? (
                      comments[project.id].map((comment, i) => (
                        <p key={i} className={`text-xs p-2 rounded-md ${
                          darkMode ? 'bg-gray-700 text-gray-300' : 'bg-gray-100 text-gray-700'
                        }`}>
                          {comment}
                        </p>
                      ))
                    ) : (
                      <p className={`text-xs italic ${darkMode ? 'text-gray-500' : 'text-gray-400'}`}>
                        Aucun commentaire pour le moment.
                      </p>
                    )}
                  </div>

                  <form
                    onSubmit={(e) => {
                      e.preventDefault()
                      const text = e.target.comment.value
                      handleCommentSubmit(project.id, text)
                      e.target.reset()
                    }}
                    className="flex gap-2"
                  >
                    <input
                      type="text"
                      name="comment"
                      placeholder="√âcrire un commentaire..."
                      className={`flex-1 px-3 py-2 text-xs rounded-lg focus:outline-none ${
                        darkMode 
                          ? 'bg-gray-700 text-white placeholder-gray-400' 
                          : 'bg-gray-100 text-gray-800 placeholder-gray-500'
                      }`}
                    />
                    <button
                      type="submit"
                      className="px-3 py-2 bg-blue-500 text-white rounded-lg text-xs hover:bg-blue-600"
                    >
                      Envoyer
                    </button>
                  </form>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Animations */}
      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(40px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </section>
  )
}
