// src/components/sections/Projects.jsx
import React from 'react';
import { useTheme } from "../../context/ThemeContext";
import ProjectCard from '../ui/ProjectCard';

const Projects = () => {
  const { darkMode } = useTheme();
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);

  // Données des projets (à remplacer par l'appel API)
  const projects = [
    {
      id: 1,
      title: "E-commerce de Location",
      description: "Plateforme de location de matériel de vacances avec système de réservation et paiement intégré.",
      image: "/images/projects/ecommerce.jpg",
      technologies: ["React", "Symfony", "TailwindCSS", "MySQL"],
      githubLink: "#",
      liveLink: "#"
    },
    {
      id: 2,
      title: "Dashboard de Gestion",
      description: "Interface d'administration pour gérer les commandes clients et suivre les modifications en temps réel.",
      image: "/images/projects/dashboard.jpg",
      technologies: ["React", "Symfony", "TailwindCSS", "MySQL"],
      githubLink: "#",
      liveLink: "#"
    },
    {
      id: 3,
      title: "Site Vitrine Restauration",
      description: "Site élégant pour un restaurant avec galerie de plats, réservation de tables et menu interactif.",
      image: "/images/projects/restaurant.jpg",
      technologies: ["React", "TailwindCSS", "JavaScript", "Responsive"],
      githubLink: "#",
      liveLink: "#"
    },
    {
      id: 4,
      title: "Plateforme de Trading",
      description: "Site de formation au trading avec système d'abonnement, cours vidéo et analyses en temps réel.",
      image: "/images/projects/trading.jpg",
      technologies: ["React", "Node.js", "Python", "TailwindCSS", "MySQL"],
      githubLink: "#",
      liveLink: "#"
    }
  ];

  // Projets de démonstration pour le fallback
  const demoProjects = [...projects];

  if (loading) {
    return (
      <section id="projects" className="py-16">
        <div className="max-w-6xl mx-auto px-4 text-center">
          <p>Chargement des projets...</p>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section id="projects" className="py-16">
        <div className="max-w-6xl mx-auto px-4 text-center">
          <p className="text-red-500">Erreur: {error}</p>
        </div>
      </section>
    );
  }

  const displayProjects = projects.length > 0 ? projects : demoProjects;

  return (
    <section id="projects" className="py-16">
      <div className="max-w-6xl mx-auto px-4">
        <div className="text-center mb-10">
          <h2 className={`text-3xl font-bold mb-3 ${darkMode ? "text-white" : "text-gray-800"}`}>
            Projets Récents
          </h2>
          <p className={`max-w-2xl mx-auto text-sm ${darkMode ? "text-gray-400" : "text-gray-600"}`}>
            Quelques-unes de mes réalisations, démontrant mes compétences en développement web front-end et back-end.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {projects.map((project, index) => (
            <ProjectCard
              key={project.id}
              project={project}
              index={index}
              darkMode={darkMode}
            />
          ))}
        </div>

        {/* Voir plus de projets */}
        <div className="text-center mt-10">
          <button
            className={`px-5 py-2 rounded-md text-sm font-medium text-white transition-colors
            ${darkMode ? "bg-blue-700 hover:bg-blue-600" : "bg-blue-600 hover:bg-blue-700"}`}
          >
            Voir plus de projets
          </button>
        </div>
      </div>
    </section>
  );
};

export default Projects;
