// src/components/sections/Skills.jsx
import React, { useEffect, useRef } from "react";
import SkillCard from "../ui/SkillCard";
import { skillCategories } from "../../data/skills";
import { useTheme } from "../../context/ThemeContext";

const Skills = () => {
  const { darkMode } = useTheme();
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("animate-fade-in");
            entry.target.style.opacity = "1";
            entry.target.style.transform = "translateY(0)";
          }
        });
      },
      { threshold: 0.1 }
    );

    const skillCards = document.querySelectorAll(".skill-card");
    skillCards.forEach((card, index) => {
      card.style.opacity = "0";
      card.style.transform = "translateY(20px)";
      card.style.transition = `opacity 0.5s ease, transform 0.5s ease ${index * 0.1}s`;
      observer.observe(card);
    });

    const titleElement = document.querySelector(".section-title");
    if (titleElement) {
      titleElement.style.opacity = "0";
      titleElement.style.transform = "translateY(-20px)";
      titleElement.style.transition = "opacity 0.8s ease, transform 0.8s ease";
      observer.observe(titleElement);
    }

    return () => {
      skillCards.forEach((card) => observer.unobserve(card));
      if (titleElement) observer.unobserve(titleElement);
    };
  }, []);

  return (
    <section
      id="skills"
      ref={sectionRef}
      className={`section-content min-h-screen flex items-center justify-center p-8 py-24 ${
        darkMode ? "bg-gradient-to-b from-gray-900 to-gray-800" : "bg-gradient-to-b from-white to-gray-50"
      }`}
    >
      <div className="max-w-7xl mx-auto px-6">
        <div className="text-center mb-20 section-title">
          <h2 className={`text-5xl font-bold mb-6 relative inline-block ${darkMode ? "text-white" : "text-gray-800"}`}>
            Expertise
            <span className="absolute -bottom-2 left-0 w-full h-1 bg-gradient-to-r from-blue-500 to-purple-600"></span>
          </h2>
          <p
            className={`max-w-2xl mx-auto text-lg ${
              darkMode ? "text-gray-300" : "text-gray-600"
            }`}
          >
            Fort d'une expertise technique approfondie et d'un sens aigu du
            design, je développe des applications web modernes et
            performantes qui répondent aux standards actuels.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
          {skillCategories.map((category, index) => (
            <div 
              key={index}
              className="skill-card"
            >
              <SkillCard 
                category={category}
                index={index}
                darkMode={darkMode}
              />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
