import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useTheme } from '../../context/ThemeContext';

const CommentSection = ({ projectId }) => {
  const navigate = useNavigate();
  const { darkMode } = useTheme();
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      if (token && userData) {
        try {
          const parsedUser = JSON.parse(userData);
          setUser(parsedUser);
        } catch (error) {
          console.error('Erreur lors du parsing des données utilisateur:', error);
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          setUser(null);
        }
      } else {
        setUser(null);
      }
    };

    checkAuthStatus();
    // Vérifier l'état d'authentification toutes les 5 secondes
    const interval = setInterval(checkAuthStatus, 5000);

    return () => clearInterval(interval);
  }, []);

  // Récupérer les commentaires
  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/comments/project/${projectId}`);
      setComments(response.data.data);
      setError('');
    } catch (error) {
      console.error('Erreur lors de la récupération des commentaires:', error);
      if (!error.response) {
        setError('Erreur de connexion au serveur. Veuillez vérifier votre connexion internet ou réessayer plus tard.');
      } else if (error.response.status === 404) {
        setError('Projet non trouvé');
      } else {
        setError('Erreur lors de la récupération des commentaires. Veuillez réessayer.');
      }
    }
  };

  useEffect(() => {
    fetchComments();
  }, [projectId]);

  // Ajouter un commentaire
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');

      if (!token || !userData) {
        setError('Veuillez vous connecter pour ajouter un commentaire');
        setTimeout(() => setError(''), 3000);
        navigate('/login');
        return;
      }

      const currentUser = JSON.parse(userData);

      const response = await axios.post('http://localhost:5000/api/comments', {
        user_id: currentUser.id,
        project_id: projectId,
        content: newComment
      }, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      if (response.data.success) {
        setNewComment('');
        setSuccess('Commentaire ajouté avec succès');
        fetchComments();
        setTimeout(() => setSuccess(''), 3000);
      } else {
        throw new Error(response.data.message || 'Erreur lors de l\'ajout du commentaire');
      }
    } catch (error) {
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
        navigate('/login');
        setError('Session expirée, veuillez vous reconnecter');
      } else {
        const errorMessage = error.response?.data?.message || error.message || 'Erreur lors de l\'ajout du commentaire';
        setError(errorMessage);
      }
      setTimeout(() => setError(''), 3000);
    }
  };

  // Supprimer un commentaire
  const handleDelete = async (commentId, commentUserId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Vous devez être connecté pour supprimer un commentaire');
        setTimeout(() => setError(''), 3000);
        return;
      }

      if (user.id !== commentUserId) {
        setError('Vous ne pouvez supprimer que vos propres commentaires');
        setTimeout(() => setError(''), 3000);
        return;
      }

      await axios.delete(`http://localhost:5000/api/comments/${commentId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setSuccess('Commentaire supprimé avec succès');
      fetchComments();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
        navigate('/login');
        setError('Session expirée, veuillez vous reconnecter');
      } else {
        setError(error.response?.data?.message || 'Erreur lors de la suppression du commentaire');
      }
      setTimeout(() => setError(''), 3000);
    }
  };

  return (
    <div className="mt-8">
      <h3 className="text-xl font-semibold mb-4">Commentaires</h3>

      {/* Formulaire d'ajout de commentaire */}
      {user ? (
        <form onSubmit={handleSubmit} className="mb-6">
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Ajouter un commentaire..."
            className={`w-full p-2 border rounded-md mb-2 ${darkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-white border-gray-300'}`}
            rows="3"
            required
          />
          <button
            type="submit"
            className={`${darkMode ? 'bg-blue-600 hover:bg-blue-700' : 'bg-blue-500 hover:bg-blue-600'} text-white px-4 py-2 rounded-md transition-colors`}
          >
            Publier
          </button>
        </form>
      ) : (
        <div className="mb-6">
          <p className={`${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>Connectez-vous pour ajouter un commentaire</p>
        </div>
      )}

      {/* Messages de succès et d'erreur */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}
      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      {/* Liste des commentaires */}
      <div className="space-y-4">
        {comments.map((comment) => (
          <div key={comment.id} className={`${darkMode ? 'bg-gray-800' : 'bg-gray-50'} p-4 rounded-md`}>
            <div className="flex justify-between items-start">
              <div>
                <p className="font-semibold">{comment.username}</p>
                <p className="text-gray-600">{comment.content}</p>
                <p className="text-sm text-gray-400 mt-1">
                  {new Date(comment.created_at).toLocaleDateString()}
                </p>
              </div>
              {user && user.id === comment.user_id && (
                <button
                  onClick={() => handleDelete(comment.id, comment.user_id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Supprimer
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CommentSection;
