// src/App.jsx (ajoutez un useEffect pour gérer les animations)
import React, { useEffect } from "react";
import { ThemeProvider, useTheme } from "./context/ThemeContext";
import useScrollSpy from "./hooks/useScrollSpy";

// Composants de mise en page
import Header from "./components/layout/Header";
import Footer from "./components/layout/Footer";
import ScrollToTop from "./components/layout/ScrollToTop";
import DotNavigation from "./components/layout/DotNavigation";

// Sections du portfolio
import Hero from "./components/sections/Hero";
import Skills from "./components/sections/Skills";
import Projects from "./components/sections/Projects";
import Contact from "./components/sections/Contact";

// Composant pour le contenu principal du portfolio
const PortfolioContent = () => {
  const { darkMode } = useTheme();
  const sections = ["home", "skills", "projects", "contact"];
  const { activeSection, showScrollTop, scrollToSection, scrollToTop } = useScrollSpy(sections);

  // Ajouter l'effet d'animation pour les éléments .reveal
  useEffect(() => {
    const revealElements = document.querySelectorAll('.reveal');

    const revealOnScroll = () => {
      revealElements.forEach(element => {
        const elementTop = element.getBoundingClientRect().top;
        const windowHeight = window.innerHeight;

        if (elementTop < windowHeight - 100) {
          element.classList.add('active');
        }
      });
    };

    // Exécuter une fois au chargement initial
    revealOnScroll();

    // Ajouter l'écouteur d'événement de défilement
    window.addEventListener('scroll', revealOnScroll);

    // Nettoyer l'écouteur d'événement
    return () => window.removeEventListener('scroll', revealOnScroll);
  }, []);

  return (
    <div
      className={`min-h-screen transition-colors duration-500 ${
        darkMode ? "bg-[#121212] text-white" : "bg-white text-gray-900"
      }`}
    >
      {/* Navigation par points sur le côté */}
      <DotNavigation 
        activeSection={activeSection} 
        scrollToSection={scrollToSection} 
      />

      {/* Header / Navigation en haut */}
      <Header 
        activeSection={activeSection} 
        scrollToSection={scrollToSection} 
      />

      {/* Sections principales du portfolio */}
      <main className="flex flex-col w-full">
        <Hero />
        <Skills />
        <Projects />
        <Contact />
      </main>

      {/* Footer */}
      <Footer />

      {/* Bouton de retour en haut */}
      <ScrollToTop 
        showScrollTop={showScrollTop} 
        scrollToTop={scrollToTop} 
      />
    </div>
  );
};

// Composant principal avec ThemeProvider
function App() {
  return (
    <ThemeProvider>
      <PortfolioContent />
    </ThemeProvider>
  );
}

export default App;
