// src/components/sections/Skills.jsx
import React, { useState } from "react";
import { useTheme } from "../../context/ThemeContext";

const Skills = () => {
  const { darkMode } = useTheme();
  const [activeCategory, setActiveCategory] = useState(0);

  const themeColors = {
    primary: darkMode ? "#6366f1" : "#4f46e5", // indigo
    secondary: darkMode ? "#8b5cf6" : "#7c3aed", // violet
    background: darkMode ? "#1f2937" : "#ffffff",
    card: darkMode ? "#374151" : "#f3f4f6",
    text: darkMode ? "#f9fafb" : "#111827",
    subtext: darkMode ? "#d1d5db" : "#4b5563",
  };

  // Crée un dégradé pour les barres de progression en fonction du niveau
  const getGradient = (level) => {
    // Plus le niveau est élevé, plus la couleur est intense
    return `linear-gradient(90deg, ${themeColors.primary} 0%, ${themeColors.secondary} ${level}%, ${darkMode ? '#4b5563' : '#e5e7eb'} ${level}%)`;
  };

  return (
    <section
      id="skills"
      className={`section-content min-h-screen flex flex-col items-center justify-center p-6 md:p-12 ${
        darkMode ? "bg-gray-900" : "bg-gray-50"
      }`}
    >
      <div className="max-w-7xl w-full">
        <div className="text-center mb-16">
          <h2 className={`text-4xl md:text-5xl font-bold mb-6 ${darkMode ? "text-white" : "text-gray-900"}`}>
            Compétences Techniques
          </h2>
          <p className={`max-w-2xl mx-auto text-lg ${darkMode ? "text-gray-300" : "text-gray-600"}`}>
            Fort d'une expertise technique approfondie et d'un sens aigu du design, 
            je développe des applications web modernes et performantes.
          </p>
        </div>

        {/* Navigation des catégories */}
        <div className="flex justify-center flex-wrap gap-4 mb-10">
          {skillCategories.map((category, index) => (
            <button
              key={index}
              onClick={() => setActiveCategory(index)}
              className={`px-6 py-3 rounded-full text-lg font-medium transition-all duration-300 ${
                activeCategory === index
                  ? `bg-gradient-to-r from-${darkMode ? 'indigo-700' : 'indigo-600'} to-${darkMode ? 'violet-700' : 'violet-600'} text-white shadow-lg`
                  : `${darkMode ? 'bg-gray-800 text-gray-300 hover:bg-gray-700' : 'bg-white text-gray-700 hover:bg-gray-100'} shadow`
              }`}
            >
              <span className="mr-2">{category.icon}</span>
              {category.title}
            </button>
          ))}
        </div>

        {/* Affichage des compétences */}
        <div className={`p-8 rounded-xl shadow-xl ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="grid gap-y-8">
            {skillCategories[activeCategory].skills.map((skill, index) => (
              <div key={index}>
                <div className="flex justify-between mb-2">
                  <span className={`font-medium ${darkMode ? 'text-white' : 'text-gray-800'}`}>
                    {skill.name}
                  </span>
                  <span className={`${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                    {skill.level}%
                  </span>
                </div>
                <div 
                  className="h-3 rounded-full w-full"
                  style={{ background: getGradient(skill.level) }}
                >
                </div>
              </div>
            ))}
          </div>

          <div className={`mt-12 p-6 rounded-lg ${darkMode ? 'bg-gray-700' : 'bg-gray-100'}`}>
            <h3 className={`text-xl font-bold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
              À propos de {skillCategories[activeCategory].title}
            </h3>
            <p className={`${darkMode ? 'text-gray-300' : 'text-gray-600'}`}>
              {activeCategory === 0 && "Conception d'interfaces utilisateur modernes et réactives avec les technologies web les plus récentes."}
              {activeCategory === 1 && "Développement de services robustes et évolutifs avec une infrastructure backend solide et performante."}
              {activeCategory === 2 && "Utilisation d'outils et de méthodologies qui optimisent le flux de travail et garantissent des livrables de qualité."}
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
