// src/components/sections/Projects.jsx
import React from "react";
import { projects } from "../../data/projects";
import { Github, ExternalLink } from "lucide-react";
import { useTheme } from "../../context/ThemeContext";

const Projects = () => {
  const { darkMode } = useTheme();

  return (
    <section id="projects" className="py-24">
      <div className="max-w-6xl mx-auto px-8">
        <div className="text-center mb-12">
          <h2 className="text-3xl font-bold mb-3">Projets</h2>
          <p
            className={`max-w-2xl mx-auto text-sm ${
              darkMode ? "text-gray-400" : "text-gray-600"
            }`}
          >
            Découvrez quelques-uns de mes projets récents, alliant technologie
            et design pour créer des expériences utilisateur exceptionnelles.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {projects.map((project, index) => (
            <div
              key={index}
              className={`rounded-xl overflow-hidden transition-all duration-300 border ${
                darkMode
                  ? "border-white/10 bg-white/5 hover:bg-white/8 hover:shadow-md"
                  : "border-gray-200 bg-white hover:shadow-md"
              }`}
            >
              <div className="relative h-40 overflow-hidden">
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
                />
                <div
                  className={`absolute top-3 right-3 px-2 py-0.5 rounded-full text-xs ${
                    darkMode
                      ? "bg-white/20 backdrop-blur-sm"
                      : "bg-black/60 text-white backdrop-blur-sm"
                  }`}
                >
                  #{index + 1}
                </div>
              </div>

              <div className="p-5">
                <h3 className="text-lg font-semibold mb-2">
                  {project.title}
                </h3>
                <p
                  className={`text-sm mb-3 line-clamp-2 ${
                    darkMode ? "text-gray-300" : "text-gray-600"
                  }`}
                >
                  {project.description}
                </p>

                <div className="flex flex-wrap gap-1.5 mb-4">
                  {project.technologies.map((tech, techIndex) => (
                    <span
                      key={techIndex}
                      className={`px-2 py-0.5 rounded-full text-xs ${
                        darkMode
                          ? "bg-white/10 text-white"
                          : "bg-gray-100 text-gray-700"
                      }`}
                    >
                      {tech}
                    </span>
                  ))}
                </div>

                <div className="flex gap-3">
                  <a
                    href={project.githubLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`inline-flex items-center gap-1.5 text-xs font-medium px-2 py-1 rounded transition-colors ${
                      darkMode
                        ? "bg-white/10 hover:bg-white/15 text-white"
                        : "bg-gray-100 hover:bg-gray-200 text-gray-800"
                    }`}
                  >
                    <Github size={12} />
                    Code
                  </a>
                  <a
                    href={project.liveLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`inline-flex items-center gap-1.5 text-xs font-medium px-2 py-1 rounded transition-colors ${
                      darkMode
                        ? "bg-white/10 hover:bg-white/15 text-white"
                        : "bg-gray-100 hover:bg-gray-200 text-gray-800"
                    }`}
                  >
                    <ExternalLink size={12} />
                    Demo
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-10 text-center">
          <a
            href="https://github.com"
            target="_blank"
            rel="noopener noreferrer"
            className={`inline-flex items-center gap-2 px-5 py-2 rounded-lg text-sm font-medium transition-colors ${
              darkMode
                ? "bg-white/10 hover:bg-white/15 text-white"
                : "bg-gray-100 hover:bg-gray-200 text-gray-800"
            }`}
          >
            <Github size={16} />
            Voir plus de projets sur GitHub
          </a>
        </div>
      </div>
    </section>
  );
};
export default Projects;
