// src/components/sections/Projects.jsx
import React from 'react';
import { useTheme } from "../../context/ThemeContext";

const Projects = () => {
  const { darkMode } = useTheme();

  // Icônes SVG pour les projets
  const Github = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-4 h-4">
      <path d="M9 19c-5 1.5-5-2.5-7-3m14 6v-3.87a3.37 3.37 0 0 0-.94-2.61c3.14-.35 6.44-1.54 6.44-7A5.44 5.44 0 0 0 20 4.77 5.07 5.07 0 0 0 19.91 1S18.73.65 16 2.48a13.38 13.38 0 0 0-7 0C6.27.65 5.09 1 5.09 1A5.07 5.07 0 0 0 5 4.77a5.44 5.44 0 0 0-1.5 3.78c0 5.42 3.3 6.61 6.44 7A3.37 3.37 0 0 0 9 18.13V22"></path>
    </svg>
  );

  const ExternalLink = () => (
    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="w-4 h-4">
      <path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6"></path>
      <polyline points="15 3 21 3 21 9"></polyline>
      <line x1="10" y1="14" x2="21" y2="3"></line>
    </svg>
  );

  // Données des projets
  const projects = [
    {
      id: 1,
      title: "E-commerce Shopify",
      description: "Boutique en ligne personnalisée avec intégration de paiement et expérience utilisateur optimisée.",
      image: "/images/projects/ecommerce.jpg",
      tags: ["Shopify", "Liquid", "JavaScript", "TailwindCSS"],
      github: "#",
      demo: "#",
      color: "blue"
    },
    {
      id: 2,
      title: "Application SaaS",
      description: "Plateforme SaaS pour la gestion de projets avec authentification et tableau de bord analytique.",
      image: "/images/projects/saas.jpg",
      tags: ["React", "Node.js", "MongoDB", "Express"],
      github: "#",
      demo: "#",
      color: "green"
    },
    {
      id: 3,
      title: "Site Vitrine WordPress",
      description: "Site vitrine responsive avec animations et optimisation SEO pour un cabinet d'architectes.",
      image: "/images/projects/wordpress.jpg",
      tags: ["WordPress", "PHP", "CSS", "jQuery"],
      github: "#",
      demo: "#",
      color: "purple"
    },
    {
      id: 4,
      title: "Application Mobile",
      description: "Application de suivi fitness avec synchronisation cloud et notifications personnalisées.",
      image: "/images/projects/mobile.jpg",
      tags: ["React Native", "Firebase", "Redux", "Expo"],
      github: "#",
      demo: "#",
      color: "indigo"
    }
  ];

  // Fonction optimisée pour obtenir les classes de couleur selon le projet
  const getColorClasses = (color, type) => {
    // Nouvelles couleurs avec moins d'opacité pour le fond en mode sombre
    const colorMap = {
      blue: {
        bg: darkMode ? "bg-gray-800" : "bg-blue-50",
        text: darkMode ? "text-blue-400" : "text-blue-700",
        border: darkMode ? "border-gray-700" : "border-blue-200",
        button: darkMode ? "bg-blue-700 hover:bg-blue-600" : "bg-blue-600 hover:bg-blue-700"
      },
      green: {
        bg: darkMode ? "bg-gray-800" : "bg-green-50",
        text: darkMode ? "text-green-400" : "text-green-700",
        border: darkMode ? "border-gray-700" : "border-green-200",
        button: darkMode ? "bg-green-700 hover:bg-green-600" : "bg-green-600 hover:bg-green-700"
      },
      purple: {
        bg: darkMode ? "bg-gray-800" : "bg-purple-50",
        text: darkMode ? "text-purple-400" : "text-purple-700",
        border: darkMode ? "border-gray-700" : "border-purple-200",
        button: darkMode ? "bg-purple-700 hover:bg-purple-600" : "bg-purple-600 hover:bg-purple-700"
      },
      indigo: {
        bg: darkMode ? "bg-gray-800" : "bg-indigo-50",
        text: darkMode ? "text-indigo-400" : "text-indigo-700",
        border: darkMode ? "border-gray-700" : "border-indigo-200",
        button: darkMode ? "bg-indigo-700 hover:bg-indigo-600" : "bg-indigo-600 hover:bg-indigo-700"
      }
    };

    return colorMap[color][type];
  };

  // Bannières pour les images avec hauteur adaptée
  const getCardBanner = (project) => {
    // Map de couleurs de fond adaptées
    const bgColors = {
      blue: darkMode ? "bg-blue-900/20" : "bg-blue-50",
      green: darkMode ? "bg-green-900/20" : "bg-green-50",
      purple: darkMode ? "bg-purple-900/20" : "bg-purple-50",
      indigo: darkMode ? "bg-indigo-900/20" : "bg-indigo-50"
    };

    return (
      <div className={`h-40 ${bgColors[project.color]} flex items-center justify-center`}>
        <div className="text-center px-4">
          <div className={`text-lg font-medium ${getColorClasses(project.color, "text")}`}>
            {project.title}
          </div>
          <div className={`text-xs mt-1 ${darkMode ? "text-gray-300" : "text-gray-600"}`}>
            {project.tags.slice(0, 2).join(" • ")}
          </div>
        </div>
      </div>
    );
  };

  return (
    <section
      id="projects"
      className="py-16"
    >
      <div className="max-w-6xl mx-auto px-4">
        <div className="text-center mb-10">
          <h2 className={`text-3xl font-bold mb-3 ${darkMode ? "text-white" : "text-gray-800"}`}>
            Projets Récents
          </h2>
          <p className={`max-w-2xl mx-auto text-sm ${darkMode ? "text-gray-400" : "text-gray-600"}`}>
            Une sélection de mes réalisations récentes mettant en avant mes compétences en développement web.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {projects.map((project) => (
            <div
              key={project.id}
              className={`rounded-lg shadow-md transition-all duration-300 hover:shadow-lg overflow-hidden ${
                darkMode ? "bg-gray-800 border border-gray-700" : "bg-white border border-gray-100"
              }`}
            >
              {/* Bannière du projet (remplace l'image placeholder) */}
              {getCardBanner(project)}

              {/* Contenu du projet */}
              <div className="p-4">
                <p className={`text-sm mb-4 ${darkMode ? "text-gray-300" : "text-gray-600"}`}>
                  {project.description}
                </p>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.tags.map((tag, index) => (
                    <span
                      key={index}
                      className={`text-xs px-2 py-1 rounded 
                        ${darkMode 
                          ? `bg-${project.color}-900/20 border border-${project.color}-800/30` 
                          : `bg-${project.color}-50 border border-${project.color}-200`
                        } 
                        ${getColorClasses(project.color, "text")}`
                      }
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                {/* Actions */}
                <div className="flex gap-3 mt-4">
                  <a
                    href={project.github}
                    className={`flex items-center gap-1.5 px-3 py-1.5 rounded text-xs font-medium text-white ${getColorClasses(project.color, "button")} transition-colors`}
                  >
                    <Github /> Code
                  </a>
                  <a
                    href={project.demo}
                    className={`flex items-center gap-1.5 px-3 py-1.5 rounded text-xs font-medium 
                    ${darkMode ? "bg-gray-700 hover:bg-gray-600 text-white" : "bg-gray-100 hover:bg-gray-200 text-gray-700"} 
                    transition-colors`}
                  >
                    <ExternalLink /> Démo
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Voir plus de projets */}
        <div className="text-center mt-10">
          <button
            className={`px-5 py-2 rounded-md text-sm font-medium text-white transition-colors
            ${darkMode ? "bg-blue-700 hover:bg-blue-600" : "bg-blue-600 hover:bg-blue-700"}`}
          >
            Voir plus de projets
          </button>
        </div>
      </div>
    </section>
  );
};

export default Projects;
