// src/components/sections/Skills.jsx
import React, { useEffect, useRef } from "react";
import SkillCard from "../ui/SkillCard";
import { skillCategories } from "../../data/skills";
import { useTheme } from "../../context/ThemeContext";
import { motion } from "framer-motion";

const Skills = () => {
  const { darkMode } = useTheme();
  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add("animate-fade-in");
          }
        });
      },
      { threshold: 0.1 }
    );

    const skillCards = document.querySelectorAll(".skill-card");
    skillCards.forEach((card) => observer.observe(card));

    return () => {
      skillCards.forEach((card) => observer.unobserve(card));
    };
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100
      }
    }
  };

  return (
    <section
      id="skills"
      ref={sectionRef}
      className={`section-content min-h-screen flex items-center justify-center p-8 py-24 ${
        darkMode ? "bg-gradient-to-b from-gray-900 to-gray-800" : "bg-gradient-to-b from-white to-gray-50"
      }`}
    >
      <div className="max-w-7xl mx-auto px-6">
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-20"
        >
          <h2 className={`text-5xl font-bold mb-6 ${darkMode ? "text-white" : "text-gray-800"}`}>
            <span className="relative">
              Expertise
              <span className="absolute -bottom-2 left-0 w-full h-1 bg-gradient-to-r from-blue-500 to-purple-600"></span>
            </span>
          </h2>
          <p
            className={`max-w-2xl mx-auto text-lg ${
              darkMode ? "text-gray-300" : "text-gray-600"
            }`}
          >
            Fort d'une expertise technique approfondie et d'un sens aigu du
            design, je développe des applications web modernes et
            performantes qui répondent aux standards actuels.
          </p>
        </motion.div>

        <motion.div 
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.2 }}
          className="grid grid-cols-1 md:grid-cols-3 gap-10"
        >
          {skillCategories.map((category, index) => (
            <motion.div 
              key={index}
              variants={itemVariants}
              className="skill-card"
            >
              <SkillCard 
                category={category}
                index={index}
                darkMode={darkMode}
              />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
