import React, { useState } from 'react';
import { Book, FileText, Users, Code, Search, ChevronRight, ExternalLink } from 'lucide-react';

const Documentation = ({ darkMode }) => {
  const [activeTab, setActiveTab] = useState('technique');
  const [searchQuery, setSearchQuery] = useState('');

  const documentations = {
    technique: [
      {
        id: 1,
        title: "Architecture Technique",
        category: "Backend",
        description: "Documentation complète de l'architecture microservices avec diagrammes et exemples de code.",
        tags: ["Node.js", "Docker", "Kubernetes"],
        lastUpdate: "15 Oct 2024",
        pages: 45,
        icon: Code,
        difficulty: "Avancé"
      },
      {
        id: 2,
        title: "API REST - Guide Complet",
        category: "Backend",
        description: "Spécifications détaillées des endpoints, authentification JWT et gestion des erreurs.",
        tags: ["REST", "API", "Express"],
        lastUpdate: "12 Oct 2024",
        pages: 32,
        icon: FileText,
        difficulty: "Intermédiaire"
      },
      {
        id: 3,
        title: "Base de Données PostgreSQL",
        category: "Database",
        description: "Schémas, migrations, indexation et optimisation des requêtes pour PostgreSQL.",
        tags: ["PostgreSQL", "SQL", "Neon"],
        lastUpdate: "10 Oct 2024",
        pages: 28,
        icon: FileText,
        difficulty: "Intermédiaire"
      },
      {
        id: 4,
        title: "Frontend React - Best Practices",
        category: "Frontend",
        description: "Patterns de design, gestion d'état, hooks personnalisés et optimisation des performances.",
        tags: ["React", "Hooks", "Performance"],
        lastUpdate: "08 Oct 2024",
        pages: 38,
        icon: Code,
        difficulty: "Intermédiaire"
      },
      {
        id: 5,
        title: "CI/CD et DevOps",
        category: "DevOps",
        description: "Pipeline de déploiement automatisé avec GitHub Actions, tests et monitoring.",
        tags: ["GitHub Actions", "Docker", "AWS"],
        lastUpdate: "05 Oct 2024",
        pages: 25,
        icon: FileText,
        difficulty: "Avancé"
      }
    ],
    utilisateur: [
      {
        id: 6,
        title: "Guide de Démarrage Rapide",
        category: "Getting Started",
        description: "Premiers pas avec l'application : inscription, configuration du profil et navigation.",
        tags: ["Débutant", "Tutorial", "Setup"],
        lastUpdate: "14 Oct 2024",
        pages: 15,
        icon: Book,
        difficulty: "Débutant"
      },
      {
        id: 7,
        title: "Gestion de Projets",
        category: "Fonctionnalités",
        description: "Créer, organiser et collaborer sur vos projets avec votre équipe.",
        tags: ["Projets", "Collaboration", "Teams"],
        lastUpdate: "11 Oct 2024",
        pages: 22,
        icon: Users,
        difficulty: "Débutant"
      },
      {
        id: 8,
        title: "FAQ et Dépannage",
        category: "Support",
        description: "Solutions aux problèmes courants et réponses aux questions fréquentes.",
        tags: ["FAQ", "Troubleshooting", "Help"],
        lastUpdate: "09 Oct 2024",
        pages: 18,
        icon: Book,
        difficulty: "Débutant"
      },
      {
        id: 9,
        title: "Fonctionnalités Avancées",
        category: "Fonctionnalités",
        description: "Automatisation, webhooks, intégrations tierces et paramètres avancés.",
        tags: ["Automation", "Integration", "Advanced"],
        lastUpdate: "07 Oct 2024",
        pages: 30,
        icon: FileText,
        difficulty: "Intermédiaire"
      },
      {
        id: 10,
        title: "Sécurité et Confidentialité",
        category: "Sécurité",
        description: "Bonnes pratiques de sécurité, gestion des accès et protection des données.",
        tags: ["Security", "Privacy", "RGPD"],
        lastUpdate: "03 Oct 2024",
        pages: 20,
        icon: Book,
        difficulty: "Intermédiaire"
      }
    ]
  };

  const tabs = [
    { id: 'technique', label: 'Documentation Technique', icon: Code },
    { id: 'utilisateur', label: 'Documentation Utilisateur', icon: Users }
  ];

  const filteredDocs = documentations[activeTab].filter(doc =>
    doc.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    doc.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
    doc.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const getDifficultyColor = (difficulty) => {
    const colors = {
      'Débutant': darkMode ? 'text-green-400' : 'text-green-600',
      'Intermédiaire': darkMode ? 'text-yellow-400' : 'text-yellow-600',
      'Avancé': darkMode ? 'text-red-400' : 'text-red-600'
    };
    return colors[difficulty] || colors['Débutant'];
  };

  return (
    <section id="documentation" className="py-24 transition-colors duration-500">
      <div className="max-w-7xl mx-auto px-6">
        {/* En-tête */}
        <div className="text-center mb-12">
          <h2 className={`text-4xl font-bold mb-4 ${darkMode ? 'text-white' : 'text-gray-900'}`}>
            Documentation
          </h2>
          <p className={`max-w-2xl mx-auto text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
            Ressources complètes pour les équipes techniques et les utilisateurs finaux
          </p>
        </div>

        {/* Onglets */}
        <div className="flex flex-wrap gap-4 justify-center mb-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all duration-300 ${
                activeTab === tab.id
                  ? darkMode
                    ? 'bg-blue-500 text-white shadow-lg shadow-blue-500/30'
                    : 'bg-blue-600 text-white shadow-lg shadow-blue-600/30'
                  : darkMode
                  ? 'bg-[#111111]/80 backdrop-blur-sm text-gray-400 hover:bg-[#1a1a1a]/80 hover:text-white'
                  : 'bg-white/80 backdrop-blur-sm text-gray-600 hover:bg-gray-100/80 hover:text-gray-900'
              }`}
            >
              <tab.icon size={20} />
              {tab.label}
            </button>
          ))}
        </div>

        {/* Barre de recherche */}
        <div className="max-w-2xl mx-auto mb-12">
          <div className="relative">
            <Search 
              className={`absolute left-4 top-1/2 transform -translate-y-1/2 ${
                darkMode ? 'text-gray-500' : 'text-gray-400'
              }`} 
              size={20} 
            />
            <input
              type="text"
              placeholder="Rechercher dans la documentation..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className={`w-full pl-12 pr-4 py-3 rounded-lg border transition-all ${
                darkMode
                  ? 'bg-[#111111]/80 backdrop-blur-sm border-gray-800 text-white placeholder-gray-500 focus:border-blue-500'
                  : 'bg-white/80 backdrop-blur-sm border-gray-200 text-gray-900 placeholder-gray-400 focus:border-blue-500'
              } focus:outline-none focus:ring-2 focus:ring-blue-500/20`}
            />
          </div>
        </div>

        {/* Grille de documentation */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredDocs.map((doc) => (
            <div
              key={doc.id}
              className={`group rounded-xl p-6 border transition-all duration-300 hover:-translate-y-1 hover:shadow-xl ${
                darkMode
                  ? 'bg-[#111111]/80 backdrop-blur-sm border-gray-800 hover:border-blue-500/40'
                  : 'bg-white/80 backdrop-blur-sm border-gray-200 hover:border-blue-400'
              }`}
            >
              <div className="flex items-start justify-between mb-4">
                <div className={`p-3 rounded-lg ${
                  darkMode ? 'bg-blue-500/10' : 'bg-blue-100'
                }`}>
                  <doc.icon className={darkMode ? 'text-blue-400' : 'text-blue-600'} size={24} />
                </div>
                <span className={`text-xs font-medium px-3 py-1 rounded-full ${
                  darkMode ? 'bg-gray-800 text-gray-300' : 'bg-gray-100 text-gray-700'
                }`}>
                  {doc.category}
                </span>
              </div>

              <h3 className={`text-lg font-semibold mb-2 ${
                darkMode ? 'text-white' : 'text-gray-900'
              }`}>
                {doc.title}
              </h3>

              <p className={`text-sm mb-4 line-clamp-2 ${
                darkMode ? 'text-gray-400' : 'text-gray-600'
              }`}>
                {doc.description}
              </p>

              <div className="flex flex-wrap gap-2 mb-4">
                {doc.tags.map((tag, index) => (
                  <span
                    key={index}
                    className={`text-xs px-2 py-1 rounded ${
                      darkMode
                        ? 'bg-gray-800 text-gray-400'
                        : 'bg-gray-100 text-gray-600'
                    }`}
                  >
                    {tag}
                  </span>
                ))}
              </div>

              <div className={`flex items-center justify-between pt-4 border-t ${
                darkMode ? 'border-gray-800' : 'border-gray-200'
              }`}>
                <div className="flex items-center gap-4 text-xs">
                  <span className={darkMode ? 'text-gray-500' : 'text-gray-500'}>
                    {doc.pages} pages
                  </span>
                  <span className={getDifficultyColor(doc.difficulty)}>
                    {doc.difficulty}
                  </span>
                </div>
                <button className={`flex items-center gap-1 text-sm font-medium transition-colors ${
                  darkMode
                    ? 'text-blue-400 hover:text-blue-300'
                    : 'text-blue-600 hover:text-blue-800'
                }`}>
                  Lire
                  <ChevronRight size={16} />
                </button>
              </div>

              <div className={`mt-3 text-xs ${
                darkMode ? 'text-gray-600' : 'text-gray-500'
              }`}>
                Mis à jour le {doc.lastUpdate}
              </div>
            </div>
          ))}
        </div>

        {/* Message si aucun résultat */}
        {filteredDocs.length === 0 && (
          <div className="text-center py-12">
            <p className={`text-lg ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
              Aucune documentation trouvée pour "{searchQuery}"
            </p>
          </div>
        )}

        {/* CTA Section */}
        <div className={`mt-16 p-8 rounded-2xl border ${
          darkMode
            ? 'bg-[#111111]/50 backdrop-blur-sm border-gray-800'
            : 'bg-white/50 backdrop-blur-sm border-gray-200'
        }`}>
          <div className="text-center max-w-2xl mx-auto">
            <h3 className={`text-2xl font-bold mb-4 ${
              darkMode ? 'text-white' : 'text-gray-900'
            }`}>
              Besoin d'aide supplémentaire ?
            </h3>
            <p className={`mb-6 ${darkMode ? 'text-gray-400' : 'text-gray-600'}`}>
              Notre équipe est disponible pour répondre à vos questions et vous accompagner
            </p>
            <div className="flex flex-wrap gap-4 justify-center">
              <a
                href="#contact"
                className={`inline-flex items-center gap-2 px-6 py-3 rounded-lg font-medium transition-all ${
                  darkMode
                    ? 'bg-blue-500 hover:bg-blue-600 text-white'
                    : 'bg-blue-600 hover:bg-blue-700 text-white'
                }`}
              >
                Nous contacter
                <ExternalLink size={16} />
              </a>
              <a
                href="https://github.com/SyFouX-dev"
                target="_blank"
                rel="noopener noreferrer"
                className={`inline-flex items-center gap-2 px-6 py-3 rounded-lg font-medium border transition-all ${
                  darkMode
                    ? 'border-gray-800 hover:bg-[#1a1a1a] text-white'
                    : 'border-gray-200 hover:bg-gray-50 text-gray-900'
                }`}
              >
                GitHub
                <ExternalLink size={16} />
              </a>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Documentation;
