// src/components/sections/Skills.jsx
import React from "react";
import SkillCard from "../ui/SkillCard";
import { skillCategories } from "../../data/skills";
import { useTheme } from "../../context/ThemeContext";

const Skills = () => {
  const { darkMode } = useTheme();

  return (
    <section
      id="skills"
      className="section-content min-h-screen flex items-center justify-center p-8 py-24"
    >
      <div className="max-w-7xl mx-auto px-6 reveal" style={{ transformStyle: 'preserve-3d' }}>
        <div className="text-center mb-20">
          <h2 className="text-4xl font-bold mb-4">Expertise</h2>
          <p
            className={`max-w-2xl mx-auto ${
              darkMode ? "text-gray-400" : "text-gray-600"
            }`}
          >
            Fort d'une expertise technique approfondie et d'un sens aigu du
            design, je d√©veloppe des applications web modernes et
            performantes.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-10" style={{ perspective: '1000px' }}>
          {skillCategories.map((category, index) => (
            <SkillCard 
              key={index}
              category={category}
              index={index}
              darkMode={darkMode}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
