// src/components/sections/Hero.jsx
import React from "react";
import SocialLinks from "../ui/SocialLinks";
import { useTheme } from "../../context/ThemeContext";

const Hero = () => {
  const { darkMode } = useTheme();
  const [isVisible, setIsVisible] = React.useState(false);

  React.useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <section
      id="home"
      className={`section-content min-h-screen flex items-center justify-center p-8 pt-20 ${isVisible ? 'visible' : ''}`}
    >
      <div className="max-w-7xl mx-auto flex flex-col lg:flex-row items-center gap-16 reveal" style={{ transform: isVisible ? 'none' : 'translateY(20px)', opacity: isVisible ? 1 : 0, transition: 'all 1s cubic-bezier(0.4, 0, 0.2, 1)' }}>
        <div 
          className="w-64 h-64 lg:w-96 lg:h-96 relative overflow-hidden rounded-3xl shadow-2xl transition-all duration-700 hover:scale-105 hover-effect"
          style={{ 
            transform: isVisible ? 'translateY(0)' : 'translateY(50px)',
            opacity: isVisible ? 1 : 0,
            transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1) 0.2s'
          }}
        >
          <div
            className={`absolute inset-0 transition-opacity duration-500 ${
              darkMode ? "opacity-30" : "opacity-0"
            }`}
            style={{
              background:
                "radial-gradient(circle at center, rgba(123, 97, 255, 0.3) 0%, rgba(123, 97, 255, 0) 70%)",
            }}
          ></div>
          <img
            src="../src/assets/avatar.jpg"
            alt="Syphax HADDOU"
            className="w-full h-full object-cover rounded-3xl"
          />
        </div>

        <div 
          className="max-w-xl text-center lg:text-left"
          style={{
            transform: isVisible ? 'translateY(0)' : 'translateY(50px)',
            opacity: isVisible ? 1 : 0,
            transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1) 0.4s'
          }}
        >
          <h1 className="text-5xl lg:text-7xl font-bold mb-6">
            Syphax{" "}
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-500 to-purple-600">
              HADDOU
            </span>
          </h1>
          <h2
            className={`text-xl lg:text-2xl mb-8 ${
              darkMode ? "text-gray-300" : "text-gray-600"
            }`}
          >
            Développeur Full Stack & Designer UI/UX
          </h2>
          <p
            className={`text-lg mb-10 leading-relaxed ${
              darkMode ? "text-gray-400" : "text-gray-600"
            }`}
          >
            Je transforme des idées complexes en expériences numériques
            élégantes et intuitives. Passionné par l'innovation et le design,
            je crée des solutions web qui marquent les esprits.
          </p>
          <SocialLinks 
            darkMode={darkMode}
            className="justify-center lg:justify-start"
          />
        </div>
      </div>
    </section>
  );
};

export default Hero;
