import React, { useState, useEffect } from "react";
import { Github } from "lucide-react";
import { projectsAPI } from "../services/api";

const ProjectsSection = ({ darkMode }) => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        setLoading(true);
        setError(null);
        const data = await projectsAPI.getAll();
        setProjects(data);
      } catch (err) {
        console.error("Erreur lors du chargement des projets:", err);
        setError("Impossible de charger les projets. Veuillez r√©essayer.");
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  // √âtat de chargement
  if (loading) {
    return (
      <section id="projects" className="py-24">
        <div className="max-w-6xl mx-auto px-8">
          <div className="text-center">
            <div
              className={`inline-block w-8 h-8 border-2 border-t-transparent rounded-full animate-spin ${
                darkMode ? "border-white" : "border-gray-900"
              }`}
            ></div>
            <p
              className={`mt-4 ${
                darkMode ? "text-gray-400" : "text-gray-600"
              }`}
            >
              Chargement des projets...
            </p>
          </div>
        </div>
      </section>
    );
  }

  // √âtat d'erreur
  if (error) {
    return (
      <section id="projects" className="py-24">
        <div className="max-w-6xl mx-auto px-8">
          <div className="text-center">
            <div
              className={`inline-flex items-center justify-center w-16 h-16 rounded-full mb-4 ${
                darkMode ? "bg-red-500/10" : "bg-red-100"
              }`}
            >
              <span className="text-2xl">‚ö†Ô∏è</span>
            </div>
            <h3
              className={`text-xl font-semibold mb-2 ${
                darkMode ? "text-white" : "text-gray-900"
              }`}
            >
              Erreur de chargement
            </h3>
            <p
              className={`mb-4 ${
                darkMode ? "text-gray-400" : "text-gray-600"
              }`}
            >
              {error}
            </p>
            <button
              onClick={() => window.location.reload()}
              className={`px-6 py-2 rounded-lg font-medium transition-colors ${
                darkMode
                  ? "bg-white/10 hover:bg-white/15 text-white"
                  : "bg-gray-100 hover:bg-gray-200 text-gray-800"
              }`}
            >
              R√©essayer
            </button>
          </div>
        </div>
      </section>
    );
  }

  // √âtat vide
  if (projects.length === 0) {
    return (
      <section id="projects" className="py-24">
        <div className="max-w-6xl mx-auto px-8">
          <div className="text-center">
            <div
              className={`inline-flex items-center justify-center w-16 h-16 rounded-full mb-4 ${
                darkMode ? "bg-gray-800" : "bg-gray-100"
              }`}
            >
              <span className="text-2xl">üìÅ</span>
            </div>
            <h3
              className={`text-xl font-semibold mb-2 ${
                darkMode ? "text-white" : "text-gray-900"
              }`}
            >
              Aucun projet disponible
            </h3>
            <p
              className={`${
                darkMode ? "text-gray-400" : "text-gray-600"
              }`}
            >
              Les projets seront bient√¥t disponibles.
            </p>
          </div>
        </div>
      </section>
    );
  }

  // Affichage normal
  return (
    <section id="projects" className="py-24">
      <div className="max-w-6xl mx-auto px-8">
        <div className="text-center mb-12">
          <h2 className="text-3xl font-bold mb-3">Projets</h2>
          <p
            className={`max-w-2xl mx-auto text-sm ${
              darkMode ? "text-gray-400" : "text-gray-600"
            }`}
          >
            D√©couvrez quelques-uns de mes projets r√©cents, alliant technologie
            et design pour cr√©er des exp√©riences utilisateur exceptionnelles.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {projects.map((project, index) => (
            <div
              key={project.id || index}
              className={`rounded-xl overflow-hidden transition-all duration-300 border ${
                darkMode
                  ? "border-white/10 bg-white/5 hover:bg-white/8 hover:shadow-md"
                  : "border-gray-200 bg-white hover:shadow-md"
              }`}
            >
              <div className="relative h-40 overflow-hidden">
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-full object-cover transition-transform duration-500 hover:scale-105"
                  loading="lazy"
                />
                <div
                  className={`absolute top-3 right-3 px-2 py-0.5 rounded-full text-xs ${
                    darkMode
                      ? "bg-white/20 backdrop-blur-sm"
                      : "bg-black/60 text-white backdrop-blur-sm"
                  }`}
                >
                  #{index + 1}
                </div>
              </div>

              <div className="p-5">
                <h3 className="text-lg font-semibold mb-2">
                  {project.title}
                </h3>
                <p
                  className={`text-sm mb-3 line-clamp-2 ${
                    darkMode ? "text-gray-300" : "text-gray-600"
                  }`}
                >
                  {project.description}
                </p>

                <div className="flex flex-wrap gap-1.5 mb-4">
                  {project.tags.map((tag, tagIndex) => (
                    <span
                      key={tagIndex}
                      className={`px-2 py-0.5 rounded-full text-xs ${
                        darkMode
                          ? "bg-white/10 text-white"
                          : "bg-gray-100 text-gray-700"
                      }`}
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                {project.link && (
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`inline-flex items-center gap-1.5 text-xs font-medium transition-colors ${
                      darkMode
                        ? "text-blue-400 hover:text-blue-300"
                        : "text-blue-600 hover:text-blue-800"
                    }`}
                  >
                    Voir le projet ‚Üí
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>

        <div className="mt-10 text-center">
          <a
            href="https://github.com/SyFouX-dev"
            target="_blank"
            rel="noopener noreferrer"
            className={`inline-flex items-center gap-2 px-5 py-2 rounded-lg text-sm font-medium transition-colors ${
              darkMode
                ? "bg-white/10 hover:bg-white/15 text-white"
                : "bg-gray-100 hover:bg-gray-200 text-gray-800"
            }`}
          >
            <Github size={16} />
            Voir plus de projets sur GitHub
          </a>
        </div>
      </div>
    </section>
  );
};

export default ProjectsSection;
