import React, { useState } from 'react'
import { useTheme } from '../../context/ThemeContext'

export default function Projects() {
  const { darkMode } = useTheme()
  const [comments, setComments] = useState({})

  const projects = [
    {
      id: 1,
      title: "My Micro Services",
      description: "Architecture de microservices pour la gestion dâ€™applications distribuÃ©es.",
      image: "https://images.unsplash.com/photo-1556761175-5973dc0f32e7?w=800&h=600&fit=crop",
      tags: ["Node.js", "Docker", "Microservices"],
      github: "https://github.com/SyFouX-dev/-my_micro_services",
    },
    {
      id: 2,
      title: "SnakeMotion Mobile",
      description: "Jeu mobile inspirÃ© du Snake, avec animations fluides et contrÃ´le tactile.",
      image: "https://images.unsplash.com/photo-1590608897129-79da98d159c7?w=800&h=600&fit=crop",
      tags: ["React Native", "Expo", "Mobile Game"],
      github: "https://github.com/SyFouX-dev/SnakeMotion-Mobile",
    },
    {
      id: 3,
      title: "Isola",
      description: "Jeu en ligne multijoueur basÃ© sur la stratÃ©gie et la rÃ©flexion.",
      image: "https://images.unsplash.com/photo-1511512578047-dfb367046420?w=800&h=600&fit=crop",
      tags: ["React", "Socket.io", "Express"],
      github: "https://github.com/SyFouX-dev/isola",
    },
    {
      id: 4,
      title: "WacKeep",
      description: "Application de gestion de tÃ¢ches et de productivitÃ© collaborative.",
      image: "https://images.unsplash.com/photo-1587613752241-30a99919a1eb?w=800&h=600&fit=crop",
      tags: ["Next.js", "MYSQL", "Tailwind"],
      github: "https://github.com/SyFouX-dev/WacKeep",
    },
    {
      id: 5,
      title: "Bot Cameo",
      description: "Bot Discord intelligent pour logs, messages et analytics serveur.",
      image: "https://images.unsplash.com/photo-1518770660439-4636190af475?w=800&h=600&fit=crop",
      tags: ["Python", "SQL", "Discord.js"],
      github: "https://github.com/SyFouX-dev/Bot-Cameo",
    },
    {
      id: 6,
      title: "Gusto Dâ€™Oro",
      description: "Site vitrine pour restaurant italien, design moderne et Ã©lÃ©gant.",
      image: "https://images.unsplash.com/photo-1600891964599-f61ba0e24092?w=800&h=600&fit=crop",
      tags: ["React", "Tailwind",],
      github: "https://github.com/SyFouX-dev/gusto-d-oro",
    },
  ]

  const handleCommentSubmit = (projectId, text) => {
    if (!text.trim()) return
    setComments((prev) => ({
      ...prev,
      [projectId]: [...(prev[projectId] || []), text],
    }))
  }

  return (
    <section
      id="projects"
      className={`py-20 sm:py-24 relative overflow-hidden transition-colors duration-500 ${
        darkMode ? 'bg-[#0b0b0b]' : 'bg-gray-50'
      }`}
    >
      {/* === FOND FLUO BLEU/VIOLET === */}
      <div className="absolute inset-0 pointer-events-none">
        <div
          className={`absolute top-20 right-10 w-72 h-72 ${
            darkMode ? 'bg-blue-500/5' : 'bg-blue-500/10'
          } rounded-full blur-3xl`}
        ></div>
        <div
          className={`absolute bottom-20 left-10 w-96 h-96 ${
            darkMode ? 'bg-purple-500/5' : 'bg-purple-500/10'
          } rounded-full blur-3xl`}
        ></div>
      </div>

      {/* === CONTENU === */}
      <div className="max-w-7xl mx-auto px-6 relative">
        {/* Titre */}
        <div className="text-center mb-14">
          <h2
            className={`text-4xl font-extrabold mb-3 tracking-tight transition-colors duration-300 ${
              darkMode ? 'text-white' : 'text-gray-900'
            }`}
          >
            Mes Projets GitHub
          </h2>
          <p
            className={`text-lg transition-colors duration-300 ${
              darkMode ? 'text-gray-400' : 'text-gray-600'
            }`}
          >
            Une sÃ©lection de mes crÃ©ations â€“ bots, jeux et sites web.
          </p>
        </div>

        {/* Grille */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <div
              key={project.id}
              className={`group overflow-hidden rounded-2xl border backdrop-blur-sm transition-all duration-500 hover:scale-[1.02] ${
                darkMode
                  ? 'bg-[#111]/80 border-[#1f1f1f] hover:border-[#3B82F6]/60 hover:shadow-[0_0_20px_rgba(59,130,246,0.25)]'
                  : 'bg-white border-gray-200 hover:border-blue-300 hover:shadow-lg'
              }`}
              style={{
                animation: `fadeInUp 0.6s ease-out ${index * 0.1}s both`,
              }}
            >
              {/* Image */}
              <div className="relative h-48 overflow-hidden">
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                />
                <div
                  className={`absolute inset-0 bg-gradient-to-t ${
                    darkMode
                      ? 'from-[#0b0b0b]/70 via-transparent to-transparent'
                      : 'from-white/80 via-transparent to-transparent'
                  }`}
                />
              </div>

              {/* Contenu */}
              <div className="p-6">
                <h3
                  className={`text-lg font-semibold mb-2 transition-colors ${
                    darkMode
                      ? 'text-white group-hover:text-[#3B82F6]'
                      : 'text-gray-900 group-hover:text-blue-600'
                  }`}
                >
                  {project.title}
                </h3>
                <p
                  className={`text-sm mb-4 transition-colors ${
                    darkMode ? 'text-gray-400' : 'text-gray-600'
                  }`}
                >
                  {project.description}
                </p>

                {/* Tags */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.tags.map((tag, i) => (
                    <span
                      key={i}
                      className={`text-xs px-3 py-1 rounded-full border transition-colors ${
                        darkMode
                          ? 'bg-[#1a1a1a] border-[#2a2a2a] text-gray-300'
                          : 'bg-gray-100 border-gray-200 text-gray-700'
                      }`}
                    >
                      {tag}
                    </span>
                  ))}
                </div>

                {/* Liens */}
                <div className="flex gap-3 mb-6">
                  <a
                    href={project.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`flex-1 flex items-center justify-center gap-2 px-4 py-2 rounded-lg text-sm font-medium transition-all duration-300 ${
                      darkMode
                        ? 'bg-gradient-to-r from-[#3B82F6] to-sky-500 text-white hover:from-sky-400 hover:to-blue-400'
                        : 'bg-gradient-to-r from-blue-600 to-sky-400 text-white hover:from-blue-500 hover:to-sky-300'
                    }`}
                  >
                    ðŸ’» Code
                  </a>
                </div>

                {/* Commentaires */}
                <div>
                  <h4
                    className={`text-sm font-semibold mb-2 ${
                      darkMode ? 'text-gray-300' : 'text-gray-700'
                    }`}
                  >
                    ðŸ’¬ Commentaires
                  </h4>

                  <div className="space-y-2 mb-3 max-h-24 overflow-y-auto">
                    {(comments[project.id] || []).length > 0 ? (
                      comments[project.id].map((comment, i) => (
                        <p
                          key={i}
                          className={`text-xs p-2 rounded-md ${
                            darkMode
                              ? 'bg-[#1a1a1a] text-gray-300'
                              : 'bg-gray-100 text-gray-700'
                          }`}
                        >
                          {comment}
                        </p>
                      ))
                    ) : (
                      <p
                        className={`text-xs italic ${
                          darkMode ? 'text-gray-500' : 'text-gray-400'
                        }`}
                      >
                        Aucun commentaire pour le moment.
                      </p>
                    )}
                  </div>

                  {/* Formulaire */}
                  <form
                    onSubmit={(e) => {
                      e.preventDefault()
                      const text = e.target.comment.value
                      handleCommentSubmit(project.id, text)
                      e.target.reset()
                    }}
                    className="flex gap-2"
                  >
                    <input
                      type="text"
                      name="comment"
                      placeholder="Ã‰crire un commentaire..."
                      className={`flex-1 px-3 py-2 text-xs rounded-lg focus:outline-none border transition-colors ${
                        darkMode
                          ? 'bg-[#1a1a1a] border-[#2a2a2a] text-gray-200 placeholder-gray-500 focus:ring-1 focus:ring-[#3B82F6]'
                          : 'bg-gray-100 border-gray-200 text-gray-800 placeholder-gray-500 focus:ring-1 focus:ring-blue-400'
                      }`}
                    />
                    <button
                      type="submit"
                      className={`px-3 py-2 rounded-lg text-xs font-medium transition-all ${
                        darkMode
                          ? 'bg-[#3B82F6] text-white hover:bg-sky-500'
                          : 'bg-blue-500 text-white hover:bg-blue-400'
                      }`}
                    >
                      Envoyer
                    </button>
                  </form>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Animation */}
      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(40px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </section>
  )
}
